css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("forum.canardpc.com") {
/*réalisé par Romain Houdry pour le forum Canard PC
-
romainhoudry.com
http://forum.canardpc.com
*/

@font-face {
  font-family: 'Open Sans';
  src: local('Open Sans'), local('OpenSans'), /* in case the font is installed */
       url(http://themes.googleusercontent.com/static/fonts/opensans/v6/cJZKeOuBrn4kERxqtaUH3T8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}

/*global*/
*{
  font-family: 'Open Sans', sans-serif !important;
  font-style: normal !important;
  border-radius: 0px !important;
  box-shadow: none !important;
  text-overflow: ellipsis !important;
}

body{
background: #f3f3f3;
}

.body_wrapper{
background: inherit;
padding: 8px 0!important;
}

/*---*/

/*SUPRABAR (tof/apc/cx)*/
#blackband{
height: 24px;
width: auto;
margin: 0 35px;
}

#blackband_subdiv{
height: inherit;
width: inherit;
margin: 0 8px;
}

#blackband_subdiv a, .barre_verticalus{
font-size: 9px;
margin: 6px 8px 0px 0px;
}

/*---*/

/*HEADER (logo, ad)*/
#header{
max-height: 96px;
}

.above_body{
margin: 0;
padding: 0;
}

.logo-image{
margin: 8px 0;
}

.ad_global_header{
display: none;
}

/*---*/

/*NAVBAR (nav, utilisateur, recherche, shortcuts)*/
#navbar{
height: 56px;
}

#navbar, #toplinks{
margin: 0;
}

#navbar a{
font-weight: normal;   
}

/*NAVBAR - "forum","quoi de neuf","derniers messages","nouveaux messages","nouveaux événements","marquer les forums comme lus"*/
.navtabs li a{
border: none !important;
background: #333 !important;
padding-top: 0px !important;
top: 0px !important;
color: #aaa;
font-size: 10px;
}

/*NAVBAR - "forum", "quoi de neuf"*/
#navtabs{
background: #333;
padding: 0px;
}

/*NAVBAR - "forum"*/
.navtabs li:first-child{
border-left: none;
}

/*NAVBAR - nav non selectionée*/
.navtabs li a.navtab{
color: #aaa;
}

/*NAVBAR - nav selectionée*/
.navtabs li.selected a.navtab, .navtabs li a.navtab:hover{
color: white;
}

/*NAVBAR > UTILISATEUR*/
#toplinks{
right: 8px;
margin: 2px 0px !important;
background: none;
}

.toplinks a{
color: #aaa!important;
}

.toplinks a:hover{
color: #fff !important;
}

.toplinks ul.isuser li{
padding-right: 2px;
}

/*NAVBAR > UTILISATEUR "Bienvenue" texte à côté du pseudo utilisateur*/
.toplinks ul.isuser li.welcomelink{
color: transparent;
}

/*NAVBAR > RECHERCHE*/
.globalsearch{
right: 14px;
top: 26px;
}

/*NAVBAR > RECHERCHE | barre de recherche cachée, raison : inefficace*/
#navbar_search{
display: none;
}

.globalsearch .textboxcontainer, .globalsearch .buttoncontainer span{
border: none;
height: inherit;
}

.globalsearch .textboxcontainer span{
border-top: none;
width: inherit;
}

.globalsearch .textboxcontainer .textbox, .globalsearch .buttoncontainer{
border: none;
}

/*NAVBAR > RECHERCHE "recherche avancée" lien*/
.navbar_advanced_search li a{
font-size: 10px;
color: #aaa;
}

/*NAVBAR "derniers messages"*/
.navtabs ul li:first-child{
margin: 0;
}

/*---*/

/*BREADCRUMB*/
#breadcrumb{
height: 24px;
font-size: 10px;
opacity: .5;
}

#breadcrumb:hover{
opacity: 1;
}

.breadcrumb .navbithome{
padding: 2px 0;
}

.breadcrumb .navbit{
padding: 0 8px 0 0px
}

/*TITRE PAGE - "forum", "discussion"*/
.above_threadlist, .above_postlist{
margin-top: 0 !important;
height: 0 !important;
}

/*ACTION (haut et bas de page) - "forum", "discussion" - "ouvrir une nouvelle discussion", "répondre à la discussion"*/
#newthreadlink_top, #newreplylink_top{
right: 0;
left: auto;
}

#newthreadlink_bottom, #newreplylink_bottom{
right: 0;
left: auto;
}

#newreplylink_top, #newreplylink_bottom{
position: absolute;
}

/*???*/
#below_postlist{ 
margin-top: 0;
margin-bottom: 0; 
}

/*PAGINATION HAUT - "forum", "discussion"*/
.threadpagenav{
top: 0 !important;
}

#pagination_top .pagination, .threadpagenav .pagination{
float: left !important;
margin-left: 0 !important;
margin-top: 40px;
}

.above_threadlist .threadpagenav, .pagination_top{
margin-top: 8px !important;
width: 100%;
}

.pagetitle{
margin-bottom: 40px
}

/*PAGINATION HAUT - "recherche", quoi de neuf"*/
#above_searchresults{
margin: 0;
}

#pagination_top{
left: 0;
float: right;
}

.searchresults{
padding-top: 40px;
}

/*PAGINATION HAUT stat - "forum", "discussion"*/
#postpagestats_above, #threadpagestats{
display: none;
}

/*---*/

/*THREAD list - "home", "forum", "recherche"*/
.childforum .forumrow, .threadbit .nonsticky, .threadbit .discussionrow, .threadbit .threadinfo, .threadbit .sticky, .threadbit .alt{
background: #eee;
}

/*THREAD head - "home", "forum", "recherche"*/
.forumhead, .threadlisthead, h2.searchlisthead{
background: #666;
font-weight: normal;
font-size: 10px;
}

/*THREAD head title - "home"*/
.forumbit_nopost .forumhead .forumtitle, .forumbit_nopost .forumhead span, .forumbit_nopost .forumhead .collapse, .forumbit_post .forumhead h2 span{
font-size: 10px;
font-weight: normal;
}

/*THREAD head stats - "forum"*/
.threadlisthead span.threadstats{
text-align: right;
width: 18%;
}

/*THREAD head lastpost - "forum"*/
.threadlisthead span.threadlastpost{
width: 14%;
padding-left: 8px;
}


/*THREAD status (icone) - "forum", "recherche"*/
.threadbit .nonsticky a.threadstatus, .threadbit .sticky a.threadstatus{
background-position: 2px 8px;
width: 48px;
}

/*THREAD bit (entrée) - "home", "forum", "recherche"*/
.forumbit_post .forumstats li, .forumbit_post .forumstats_2 li, .threadbit .threadpostedin p,.threadbit .threadstats{
font-size: 9px;
text-align: right;
}

/*THREAD info - "forum", "recherche"*/
.threadbit .threadinfo{
width: 70%;
}

/*THREAD stats - "forum", "recherche"*/
.threadbit .threadstats{
width: 10%;
}

.threadbit .threadstats li{
margin: 2px 0;
}

/*THREAD posted in - "recherche"*/
.threadbit .threadpostedin{
display:none;    
}

/*THREAD last post - "forum", "recherche"*/
.threadbit .threadlastpost{
width: 18%;
padding: 8px 0px 0 8px;
font-size: 9px;
}

/*THREAD last post - "recherche"*/
.threadbit .threadlastpost dd{
margin: 0;
}

/*THREAD meta - "forum", "recherche"*/
.threadbit .threadmeta{
font-size: 10px;
color: #666;
height: 16px;
}

/*THREAD meta auteur, date - "forum", "recherche"*/
.threadbit .threadmeta .author span.label{
padding-left: 4px;
}

/*---*/

/*PAGINATION BAS - "forum", "discussion"*/
#pagination_bottom .pagination{
float: left;
margin-left: 0;
}

/*PAGINATION BAS navigation rapide - "forum", "discussion"*/
#forumdisplay_navpopup, #showthread_navpopup{
display: none;
}

/*???*/
.forumfoot{
height: 0;
}

.below_threadlist .threadpagenav{
padding: 0;
}

/*PAGINATION BAS - "recherche", "quoi de neuf"*/
#below_searchresults{
float: left;
}

/*---*/

/*TOPIC - container liste de messages*/
#postlist{
width: 100%;
margin: 0 auto;
border-radius: 2px;
background: inherit;
}

.postcontainer{
border: none;
margin-bottom: 0;
}

.postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead {
background: #c5c5c5;
border: 10px #888;
padding: 6px 0px;
margin: 0;
}

.nodecontrols{
text-align: right !important;
padding: 8px 10px 0 !important;
}

.postbitlegacy .userinfo {
width: 120px;
background: #eee;
padding: 0px 10px;
}

.userinfo_extra dt{
margin: 0px !important;
min-width: 0px !important;
}

.postbitlegacy .postbody, .eventbit .eventdetails .eventbody {
margin-left: 0px;
background: #eee;
border: none;
}

.postrow{
max-width: inherit;
padding: 1em 1em 1em 0em !important;
}

blockquote{
margin-left: 10px;
font-size: 13px;
line-height: 20px;
}

/*TOPIC - signature*/
.signature{
border-top: 1px solid #ddd;
font-size: 9px;
}

.signature a{
font-size: 9px;
}

/*TOPIC - contrôle conversation*/
.textcontrols{
height: 10px;
padding: 12px 0px !important;
background: inherit;
opacity: 0.5;
}

.textcontrols:hover{
opacity: 1;
}

.postcontrols{
text-align: right !important;
}


/*---*/

/*USER colonne gauche*/
#sidebar_container .userprof_content .blockrow{
background-color: inherit;
}

/*USER tableau de bord*/
#usercp_content{
margin-top: 5px !important;
}

/*USER tableau de bord last post */
#usercp_content .threadbit .threadlastpost{
width: 18%;
}

/*---*/

/*HOME*/
/*Future CSS goes here*/
}
