css:
@-moz-document domain("amboss.miamed.de") {
@media print {
    
/* *** Sprechblasen *** */

.dictionary {
  text-decoration:none; border-bottom: 1px dashed black;
    
}
  

 
.leitwort, .wichtig, .ankerlink {
 font-weight: bolder;
    font-style: oblique;
    
}

.impprelevanz1, .impprelevanz2, .impprelevanz3 {
  background-color: #FAFB9C;
    font-weight: bolder; 
    font-style: oblique;
    
}
 
 
/*  Betonung der markierten Bereiche mit grau
.impprelevanz1 {
  background-color: #FFFFFF;
    font-weight: bolder;
    border-bottom: 1px dashed black;
      
}
*/     
small {
    
     display: none !important;
}

a.ankerlink_invisible {
     display: none !important;
    
}

    
hr {
    
    display: none !important;
    
}
  
    

h3 {
    
 	page-break-after: avoid; 
    text-decoration: underline;
}
    
/* *** Allgemeine Einstellungen *** */

.image img {
display: none !important;

                   
}
    
    
span.inactive {
    color: #BDBABA;
    
}
aside.rating {
 margin-left: 770px;   
}


     
table {
    page-break-inside: avoid;}

p {page-break-inside: avoid;
	page-break-after: avoid;
}
  
header {page-break-after: avoid;
text-decoration: underline;
    
    
   
}

section {page-break-inside: avoid;} 

    list {page-break-inside: avoid;} 
    
body {
    font-size: 12px;
    display:inline ! important;
    font-family: "Lucida Grande","Lucida Sans Unicode",Helvetica,Arial,Verdana,sans-serif
}

h1 {
    font-size: 24px;
    text-decoration: underline;
    text-align: center;
    font-weight: bold;
    
}
    
h2 {

    text-align: center;
    text-decoration: underline;
    font-weight: bold;
}
   

    
.autolink {
    text-decoration: none !important;
    color: black !important;
}
    

    

    
article table
{
    border-collapse:collapse !important;
}
article table th, article table td {
    border: 0.1em solid #C0C0C0 !important;
    padding: 0.3em !important;
    margin: 0em !important;
    vertical-align: top !important;
}
article table ul {
    padding-left: 1em !important;
}
article span.merke {
    border: 0.3em solid green !important;
    display: block !important;
    margin: 0.3em !important;
    padding: 0.5em 0.5em 0.5em 1em !important;
}
article span.cave {
    border: 0.1em solid red !important;
    display: block !important;
    margin: 0.3em !important;
    padding: 0.5em 0.5em 0.5em 1em !important;
}

span.veraltet, span.unwichtig {
    font-size: 10px !important;
    color: #444444 !important;
}
span.veraltet > .autolink, span.unwichtig > .autolink {
    text-decoration: none !important;
    color: #444444 !important;
}


/* *** Auszublendende Elemente *** */
#gn-menu {
display: none ! important;
}

#amboss-sidebar {
display: none ! important;
}
#Main {
    display: none !important;
}
#LibrarySidemenu {
	display: none !important;
}
.Tools {
	display: none !important;
}
.close {
    display: none !important;
}
footer {
	display: none !important;
}

.Feedback{
    display: none !important;
}
.Date {
	display: none !important;
}
button {
	display: none !important;
}



.youtube-player {
    display: none !important;
}
    


} /* @media print */
}
