css:
@-moz-document domain("younow.com"){
/*background color*/
body {
background-color: #222 !important;
}

/*leftsidebar bg color*/
div#leftsidebar {
background-color: #222 !important;
}


/*leftsidebar username color*/
#leftsidebar .trending-user {
color: #3cf !important;
}

/*leftsidebar viewer color*/
#leftsidebar .viewerCount {
color: #fcf !important;
}

/*leftsidebar expand trending*/
#leftsidebar .trending-users {
height: 220px !important;
}

/*leftsidebar trending tags color*/
#leftsidebar .trending-tag {
color: #3cf !important;
}

/*Player header bg color*/
#playerheader {
background-color: #333 !important;
}

/*Player header detail bg color*/
.line-clamp:after {
background: none !important;
}

/*Player header name & lvl color*/
#playerheader .broadcaster-name, #playerheader .broadcaster-name span, #playerheader .short-text, #playerheader .broadcaster-setup-description {
color: #fcf !important;
}

/*Player header tag color*/
#playerheader .broadcaster-tag {
color: #3cf !important;
}

/*Player bottom bar bg color*/
#settingup-toolbar, .player-toolbar, .player-toolbar-right, #playerfooter {
background-color: #333 !important;
}

/*player bottom bar like image*/
#toolbar-like-button img {
content: url(http://i.imgur.com/HBcDMNP.png) !important;
}

/*player bottom bar uptime + viewers*/
.player-toolbar-right span, .toolbar-like-count, .share-like-count, .player-toolbar-right .ynicon, .ynicon-momentcreation:before, .ynicon-btn-bc-share-android:before {
color: #3cf !important;
}

/*player size*/
object#flashObj1 {
width: 790px ;
height: 590px ;
}

/*full screen player size*/
.fullscreen.fullscreen-wide object, .fullscreen.fullscreen-wide video, .fullscreen.fullscreen-wide canvas {
width: 100vw !important;
height: 75vw !important;
margin-top: calc((75vw - 100vh)/(-2)) !important;
}

/*player/user/chat width + height*/
#main {
width: 1500px !important;
height: 1424px !important;
}

/*main page width*/
.main-content {
width: 1230px !important;
}

/*player header width*/
#playerheader {
width: inherit !important;
}

/*player bottom bar width*/
div#playerfooter {
width: inherit !important;
}

/*player bottom bar allignment width*/
div.player-toolbar {
width: 780px !important;
}

/*chat height & bg color*/
#sidebar-container {
background-color: #333 !important;
height: 730px !important;
}

/*chat height*/
#sidebar-container .sidebar-tab {
height: 581px !important;
}

/*chat height*/
#sidebar-container .chatcomments-large #chatcomments {
height: 581px !important;
background-color: #333 !important;
}

/*chat height*/
#sidebar-container .chatcomments-medium #chatcomments {
height: 378px !important;
}

/*chat height*/
#sidebar-container .chatcomments-small #chatcomments {
height: 265px !important;
}

/*chat height*/
#sidebar-container .chatcomments-mediumlarge #chatcomments {
height: 468px;
}

/*chat name color*/
#sidebar-container #topfan-slider {
color: #3cf !important;
width: 400px !important;
margin: 0 10px !important;
}

/*chat text*/
#chatcomments span {
color: #3cf !important;
}

/*chat lvl star color*/
#sidebar-container #chatcomments .ynicon-level {
color: #fcf !important;
}

/*chat lvl number color*/
/*b, strong {
color: #fcf !important;
}*/

/*chat name color*/
#sidebar-container #chatcomments .chat-name, #sidebar-container #chatcomments .ynicon-level {
color: #fcf !important;
}

/*chat submit background color*/
#sidebar-container #chatinput {
border: 1px solid #222 !important;
border-left: none !important;
border-right: none !important;
z-index: 1000 !important;
background-color: #333 !important;
}

/*chat input background color*/
#sidebar-container #chatinput input {
background: #555 !important;
color: #3cf !important;
padding-left: 5px !important;
}

/*chat gift tray bg color*/
#sidebar-container #gifttray .gifttray-basic {
background-color: #333 !important;
}

/*top navigation bar bg color*/
.navbar {
background-color: #333 !important;
}

/*top navigation bar younow logo bg color*/
.navbar-content .nav-logo {
background-color: #3cf !important;
}

/*chat CHAT, AUDIENCE, GUEST selected button color*/
#sidebar-container .sidebar-header .tab-icon.selected {
background-color: #fcf !important;
}

/*chat CHAT, AUDIENCE, GUEST not selected button color*/
#sidebar-container .sidebar-header .tab-icon {
background-color: #3cf !important;

}

/*chat AUDIENCE, GUEST number/text color*/
#sidebar-container .sidebar-tab>div, #guestpanel .guest-nav .guest-total, #guestpanel .guest-nav .btn-group .btn .text-muted {
color: #3cf !important;
}

/*chat GUEST sort by color*/
#guestpanel .guest-nav .btn-group .btn {
color: #3cf !important;
}

/*chat gift border color*/
#sidebar-container #gifttray .toggle-tray-btn {
border-left: 1px solid #222 !important;
}

/*search results bg color*/
.search-box .dropdown-menu, .search-title {
background-color: #333 !important;
}

/*search results bg color hover*/
.search-box .active {
background-color: #555 !important;
}

/*search result description color*/
.searchResult .userInfo .description, .searchResult .description .description {
color: #3cf !important;
}

/*search result user name color*/
.searchResult .userInfo .name span, .searchResult .description .name span {
color: #fcf !important;
}

/*chat & search result lvl icon color*/
.ynicon-level:before {
color: #fcf !important;
}

/*chat bg color*/
#sidebar-container .chatcomments-mediumlarge #chatcomments {
background-color: #333 !important;
}

/*chat next fan button image*/
#nextfan img {
content: url(http://i.imgur.com/hTHpOsi.png) !important;
}

/*chat previous fan button image*/
#prevfan img {
content: url(http://i.imgur.com/RXS6nUX.png) !important;
}

/*chat top fan border color*/
#sidebar-container .topfan-slider-divider {
border-bottom: 1px solid #222 !important;
}

/*chat previous fan button width*/
#sidebar-container .next-fan, #sidebar-container .prev-fan {
width: 29px !important;
}

.activity-panel .status span, [activity-panel] .status span {
color: #fcf !important;
}

.activity-panel .status .current-activity, [activity-panel] .status .current-activity {
color: #3cf !important;
}

#tag-queue .title a {
color: #3cf !important;
}

#tag-queue .title {
color: #fcf !important;
}

#audiencepanel .viewer .viewer-name {
color: #fcf !important;
}

#audiencepanel .viewer .viewer-location span, #audiencepanel .viewer .fans-active .fans {
color: #3cf !important;
}


#guestpanel .guest .summary>*, #guestpanel .guest .summary-placeholder>* {
color: #fcf !important;
}

/*user info panel bg color*/
.modal-body {
background: #333 !important;
}

/*user info panel bg color*/
.profile-summary-wrapper .actions {
background: #333 !important;
border-top: 1px solid #555 !important;
}

/*user info panel border color*/
.modal-header {
border-bottom: 1px solid #555 !important;
}

/*user info panel user name color*/
.profile-summary-wrapper .user-info .name {
color: #fcf !important;
}

/*user info panel description text color*/
.profile-summary-wrapper .summary {
color: #3cf !important;
}

/*user info panel fan count color*/
.profile-summary-wrapper .user-bio, .profile-summary-wrapper .action-notifications {
color: #3cf !important;
}

/*user info panel location color*/
#modalWindow > div > div > div > div.modal-body > div > div.user-bio.line-clamp > span.location {
color: #3cf !important;
}

/*ads*/
.under-chat {
display: none !important;
}

.main-channel-detail {
margin-left: 0px !important;
}

/*chat audience table height*/
#audiencepanel .audience {
height: 656px !important;
}

/*player loading overlay*/
.player-overlay .transition-overlay .thumbnail-pulse, .player-overlay .transition-overlay {
display: none !important;
}

/*Top Fan Padding*/
#sidebar-container #topfan-slider .top-fan{
padding-left: 17px !important;
overflow: hidden !important;
}

.intro-video-overlay {
display: none !important;
width: 790px !important;
height: 590px !important;
}

intro#videointro {
display: none !important;
width: 790px !important;
height: 590px !important;
}

div.intro-video-overlay.ng-scope {
display: none !important;
width: 790px !important;
height: 590px !important;
}

/*audio, canvas, progress, video {
display: none !important;
width: 790px !important;
height: 590px !important;
}*/

.tansition-overlay {
display: none !important;
width: 790px !important;
height: 590px !important;
}

video#introvideo {
display: none !important;
width: 790px !important;
height: 590px !important;
}

div.thumbnail-pulse.circle-thumb.ng-scope {
width: 790px !important;
height: 590px !important;
}

div.transition-overlay.ng-scope {
/*display: none !important;*/
width: 790px !important;
height: 590px !important;
}

div.user-info.ng-binding.ng-scope {
width: 790px !important;
}

div.transition-text.ng-scope {
width: 790px !important;
height: 590px !important;
}

/*.player-overlay .premium-overlay, .player-overlay .gift-message, .player-overlay .premium-gift {
width: 790px !important;
height: 590px !important;
}*/

/*chat guest list height*/
#guestpanel .guest-list {
height: 689px !important;
max-height: 689px !important;
}

/*chat guest list height*/
#sidebar-container .sidebar-tab {
height: 689px !important;
}

/*chat guest list lvl color*/
div.guest-name.short-text.clickable > span > span.chat-name.level {
color: #fcf !important;
}

/*Player Overlay Text Width*/
.system-message {
max-width: 790px !important;
}

/*Player Likes Overlay*/
.premium-overlay {
width: 790px !important;
height: 590px !important;
}

.player-overlay {
width: 790px !important;
height: 590px !important;
}

/*player likes/gift overlay*/
.player-overlay .premium-overlay .gift-overlay {
position: absolute !important;
bottom: 3% !important;
}

/*guest invite overlay*/
.mini-overlay .connecting-guest, .mini-overlay .inviting-guest, .mini-overlay .inviting-request, .mini-overlay .inviting-guest-audience, .mini-overlay .ending-guest, .mini-overlay .swapping-guest, .mini-overlay .invite-declined, .mini-overlay .invite-timeout {
width: 395px !important;
height: 590px !important;
}

/*guest invite overlay*/
.mini-overlay {
width: 395px !important;
height: 590px !important;
}

/*player user bio width*/
#playerheader .broadcaster-description {
width: 650px !important;
top: 30px !important;
left: 70px !important;
position: absolute !important;
}

/*player user bio link color*/
#playerheader .broadcaster-description a {
color: #3cf !important;
}

/*player size*/
.main-content .main-player {
    float: left;
    position: relative;
    border-radius: 5px;
    width: 790px;
    margin: 0 20px;
    margin-right: 20px;
}

.player-main {
    height: 590px!important;
}

/*flash size*/
.no-crop #flashObj1 {
    width: 790px !important;
}
}

@-moz-document domain("www.younow.com") {
/*Explore Page*/


/*explore lvl & name color*/
.main-content.explore-content .result .name {
color: #fcf !important;
}

/*explore view more button color*/
.main-content.explore-content .explore_results .explore_more {
background: #3cf !important;
}


/*explore header color*/
.main-content.explore-content .explore_header {
color: #555 !important;
}

/*explore editors choice user name color*/
.column-list .column-scroll .list-item .description {
color: #3cf !important;
}

/*explore editors choice header color*/
.column-list .title {
color: #555 !important;
}

/*explore best broadcasters bg & border color*/
.main-content.explore-content .explore_results.tag .result {
background: #222 !important;
border: solid 1px #555 !important;
}

/*explore best broadcasters become a fan button color*/
.btn-fan, .btn-subscribe {
background:  #222 !important;
}

/*user full profile page*/


/*user profile main content bg color*/
.main-content.main-async .main-content-left .user-summary {
background: #333 !important;
}

/*user profile main content lvl & username color*/
.main-content.main-async .main-content-left .user-summary .name .short-text, .main-content.main-async .main-content-left .name .a, .main-content.main-async .main-content-left .name span {
color: #fcf !important;
}

/*user full profile button border color*/
.main-content.main-async .main-content-left .navigation.notSubscribable>ul {
border: solid 1px #222 !important;
}

/*user full profile button border color*/
.main-content.main-async .main-content-left ul.nav>li:first-child, .main-content.main-async .main-content-left ul.nav>li {
border: solid 1px #222 !important;
}

/*user full profile description color*/
#main > div.main-content > div:nth-child(2) > div > div.main-content-left > div.user-summary > div.user-description > div.user-bio-section > div > span {
color: #3cf !important;
}

/*user full profile comment box bg color*/
.comment-container, .moment-container {
background: #333 !important;
}

/*user full profile comment box header color*/
div.description > span > span:nth-child(1) {
color: #3cf !important;
}

/*user full profile comment box user name color*/
.main-content.main-async .main-content-left .name {
color: #fcf !important;
}

/*user full profile button bg color*/
.main-content.main-async .main-content-left .navigation.notSubscribable>ul {
background: #333 !important;
}

/*user full profile replay video border color*/
.archived-broadcasts .broadcast-summary {
border: 1px solid #555 !important;
}

/*user full profile replay video title color*/
.archived-broadcasts .title {
color: #3cf !important;
}

/*user full profile replay video viewer/like/comments/gift color*/
.comment-container .comment-media .broadcast .stats div span, .moment-container .comment-media .broadcast .stats div span {
color: #fcf !important;
}
/*user full profile replay video comment bg color*/
.comment-container .editor, .moment-container .editor {
background: #555 !important;
}

/*user full profile replay video comment box border color*/
.comment-container .comment-area, .moment-container .comment-area {
border-top: 1px solid #222 !important;
}

/*user full profile discussion tab comments color*/
.comment-container, .moment-container {
color: #3cf !important;
}

/*user full profile fans of table bg color*/
.fans-tab .fans-tab-fan, .fans-of-tab .fans-tab-fan {
background: #333 !important;
}

/*user full profile fans of description color*/
.fans-tab .description, .fans-of-tab .description {
color: #3cf !important;
}

/*user full profile description color*/
#main > div.main-content > div > div:nth-child(2) > div > div.main-content-left > div.user-summary > div.user-description > div.user-bio-section > div > span:nth-child(1) {
color: #3cf !important;
}
}
