css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.google.nl") {

/* hide 'related searches' */
div.e {opacity: 0.10;}

/* hide 'Translate this page' */
span.std {display:none;}

/* hide 'related searches' or 'related news' */
#res.med div ol li div.g {opacity: 0.25;}

/* hide 'Hint: Search in your lang' */
div#res.med p.std {opacity: 0.10;}

/* hide 'Related searches' (at top) */
div#res.med div#trev {opacity: 0.10;}

/* hide ad text (sponsored links, your ad here, etc) */
table#mbEnd td#rhsline p, table#mbEnd td#rhsline h2 {display:none;}
div#tads h2 {display:none;}

}
