css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("libsdl.org") {
  *{
    background: black !important;
    color: white !important;
    box-shadow: 0px 0px 0px 0px !important;
  }
  .table-of-contents{
    border-radius: 5px !important;
  }
  
  #searchinput{
    border: solid white 1px;
    border-radius: 1px;
    width: 100% !important;
  }
  #searchbuttons{
    display:block;
  }
  #searchbuttons *{
    width: 50%;
    margin: 0%;
  }
  
  .codearea {
    font-weight: bold;
    font-size: smaller;
  }
  .c1 { /* Comment */
    color: #814682 !important;
  }
  .cp { /* Preprocessor */
    color: #d455aa !important;
  }
  .k { /* Keyword */
    color: #ec691e !important;
  }
  .kt { /* Keyword: type */
    color: #9cf828 !important;
  }
  .n { /* Name */
    color: #9acd9a !important;
  }
  .nf { /* Name: function */
    color: #e6b351 !important;
  }
  .p, .o { /* Punctuation and operators */
    color: white !important;
  }
  .s, .se, .mi { /* Literals */
    color: #477488 !important;
  }
}
