@-moz-document domain("wiki.guildwars2.com") {
body {
    background: #fff;
}
a {
    color: #9e2e2e;
}
a:visited {
    color: #c00;
}
a.new,
a.new:visited {
    color: #777;
}
::selection {
    background: #c00;
    color: #f97;
}
::-moz-selection {
    background: #c00;
    color: #f97;
}
* {
    transition: opacity 300ms ease-in-out 200ms;
}
#siteNotice,
#p-personal,
#p-navigation,
#p-Support,
#p-tb,
#p-lang,
#p-search > h3 {
    display: none;
}
#p-cactions {
    opacity: .3;
}
#p-cactions:hover {
    opacity: 1;
}
#p-logo {
    position: fixed;
    z-index: auto;
}
#p-logo a {
    margin-left: calc(10vw - 6.25em);
    margin-top: -15px;
}
#p-logo::before {
    content: " ";
    position: absolute;
    width: 100vw;
    height: 175px;
    z-index: -1;
    background-image: url(https://wiki.guildwars2.com/images/e/e2/Skin_header_%28Path_of_Fire%29.png);
    background-attachment: fixed;
    background-repeat: no-repeat;
}
#p-logo a,
#p-logo:hover a {
    background-size: 50%;
    background-position: 50% 25% !important;
}
#p-search {
    position: fixed !important;
    top: 70px;
    margin-left: calc(10vw - 5.5em);
}
.leaflet-container {
    height: 80vh !important;
    width: 80% !important;
    margin-left: 10%;
}
.infobox .leaflet-container {
    width: 100% !important;
    height: 40vh !important;
}
.infobox p[style*="font-size:.9em;"] {
    display: none;
}
div.infobox .image_wrapper a img,
div.infobox table a.image img {
    width: auto !important;
}
div#content {
    width: 70vw;
    margin-left: 20vw !important;
}
div#upperBar {
    left: calc(20vw + 1em);
}
div#p-cactions {
    margin-left: calc(20vw + 1em) !important;
}
#p-cactions .pBody {
    width: auto;
}
#toc,
.toc {
    position: fixed;
    top: 5px;
    right: 10px;
    z-index: 1;
    border-radius: 2px 3px 4px 7px;
    border: none;
    background-color: #fff;
    box-shadow: -5px 5px 10px -3px hsla(1, 0%, 0%, .2);
}
#toc ul,
.toc ul {
    height: 0px;
    opacity: .0;
    transition: all 0ms ease-in-out 0ms;
}
#toc:hover ul,
.toc:hover ul {
    height: auto;
    max-height: 600px;
    overflow: auto;
    opacity: 1;
    transition: all 300ms ease-in-out 200ms;
}
div.infobox:nth-child(1),
div.infobox:nth-child(2) {
    position: fixed;
    top: 145px;
    left: 0;
    margin: 0;
    width: 20vw !important;
    overflow-y: auto;
    overflow-x: hidden;
    height: calc(100vh - 145px);
    border: none;
    background: #fff;
}
div.infobox:nth-child(1)::after,
div.infobox:nth-child(2)::after {
    display: none;
}
div.infobox .heading {
    color: #d16161 !important;
    background-color: transparent !important;
    text-shadow: hsla(1, 69%, 24%, .0) 0 0 .25em !important;
}
div.infobox.guardian .heading {
    background-color: transparent !important;
    color: hsl(180, 67%, 36%) !important;
}
div.infobox.mesmer .heading {
    background-color: transparent !important;
    color: hsl(296, 100%, 33%) !important;
}
div.infobox.warrior .heading {
    background-color: transparent !important;
    color: hsl(27, 100%, 70%) !important;
}
div.infobox.engineer .heading {
    background-color: transparent !important;
    color: hsl(29, 100%, 31%) !important;
}
div.infobox.ranger .heading {
    background-color: transparent !important;
    color: hsl(89, 100%, 33%) !important;
}
div.infobox.thief .heading {
    background-color: transparent !important;
    color: hsl(359, 72%, 50%) !important;
}
div.infobox.elementalist .heading {
    background-color: transparent !important;
    color: hsl(359, 100%, 71%) !important;
}
div.infobox.necromancer .heading {
    background-color: transparent !important;
    color: hsl(133, 73%, 33%) !important;
}
div.infobox .image_wrapper a img,
div.infobox table a.image img {
    width: 100%;
    height: auto;
}
div.infobox dt {
    width: 35%;
    overflow: hidden;
    padding-left: 0;
    padding-right: 3%;
}
div.infobox dt:hover {
    word-break: break-all;
}
div.infobox dd {
    width: 57%;
    margin: 0;
    padding-left: 2%;
    padding-right: 0;
}
input#mw-searchButton,
input#searchGoButton,
input.searchButton {
    display: block;
    padding: 5px 0;
    margin: 0 2px 0 0;
    width: calc(50% - 15px);
    text-align: center;
    line-height: normal;
    float: right;
    border: none;
    box-shadow: 0px 10px 14px -7px #73275f;
    background-color: #3d053c;
    border-radius: 3px;
    cursor: pointer;
    color: #ffffff;
    text-decoration: none;
    text-shadow: 0px 1px 0px #4d7380;
}
input#mw-searchButton:hover,
input#searchGoButton:hover,
input.searchButton:hover {
    background-color: #76324f;
}
input#mw-searchButton:active,
input#searchGoButton:active,
input.searchButton:active {
    position: relative;
    top: 1px;
}
input#mw-searchButton {
    width: calc(50% + 10px);
    float: left;
    margin-right: 0;
}
div#footer {
    padding-left: calc(20vw + 1.5em) !important;
}
}
@-moz-document domain("wiki.guildwars2.com") {
/* Dark theme begins */
/*[[darktheme]]*/
}

