css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("willusher.io") {
  *{
    background: black !important;
    color: white !important;
  }
  h1, h2, h3, .h1, .h2, .h3 {
    margin: 0px !important;
    padding: 5px;
    font-size: medium;
  }
  h1, h2, .page-header {
    font-weight: bold;
    font-size: large;
  }
  .page-header {
    margin:0px;
    margin-bottom: 5px;
    padding:0px;
    border: solid #555 1px;
    border-radius: 4px;
    
  }
  .page-header h1, h2{
    margin: 10px;
    background: #222 !important;
  }
  .page-header h1 {
    text-align: center;
    padding-top: 10px;
    padding-bottom: 10px;
  }
  .col-md-12, .col-md-9{
    padding-left: 0px;
    padding-right: 0px;
  }
  .navbar {
    border-bottom: solid #555 1px;
    margin-bottom: 10px !important;
    min-height: unset !important;
  }
  .navbar-brand, .navbar-nav li a{
    height: unset !important;
    padding: 10px !important;
    line-height: 1.0 !important;
  }
  .nav, .navbar, .navbar-inverse, .navbar-static-top, .navbar-nav li a, .navbar-header, .navbar-toggle, 
  .navbar-brand, .navbar-right, .navbar-nav, .collapse, .navbar-collapse, .navbar .container {
    font-weight: bold;
    line-height: 1.0 !important;
    background: #222 !important;
  }
  .col-md-3{
    border: solid #555 1px;
    border-radius: 4px;
  }
  .col-md-9 {
    text-align: justify;
    padding-right: 5px;
  }
  code {
    color: white !important;
    font-weight: bold;
  }
  .comments {
    display: none;
  }
}
