css:
@-moz-document domain("neogaf.com") {
/* latin */
@font-face {
  font-family: 'Muli';
  font-style: normal;
  font-weight: 300;
  src: local('Muli Light'), local('Muli-Light'), url(https://fonts.gstatic.com/s/muli/v7/mls_29xz6YyJHsylG8hcCgLUuEpTyoUstqEm5AMlJo4.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}

.post { font-size: 18px; line-height: 1.5em; font-family: 'Muli', serif; font-weight: 300; color: #656565; }

.quote { color: #999; font-weight: 300; font-style: italic; margin-left: 0 !important; padding: 12 28px !important; border-radius: 6px }

.postbit-post { padding: 10px 24px; }

a { -webkit-transition: all 0.15s ease; }
  a:hover { color: #E80; }
}
