@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cocoronote.com") {
    .blog-title-outer,
    #sub, #extra,
    #globalnavi,
    div[class*="ad-"],
    .osusume-rss,
    .rss, .rss2, .rss3, .ga-rss2,
    .acrrank2, 
    .kotei-link,
    .footer-outer,
    #topcontrol,
    span[id*="_yad_"],
    .related-articles,
    .social-buttom-bigsize,
    .social-buttom,
    #ad2,
    .trackbacks,
    #comments-list,
    #comment-form,
    .gazooo,
    iframe,
    .pager,
    div.article-body > div:first-child,
    div.article-body-inner > div.t_h:first-child,
    div.ga-rss,
    p[style="margin:0;"],
    div[id^="ldblog_related_articles"],
    dl.article-tags,
    #article-options
    {
      display : none !important;
    }
    
    #content
    {
    margin: 10px auto !important;
    }
    
    #container
    {
    width: 800px !important;}
    
    .column-inner
    {
    margin: 0 auto !important;
    }
    
    div.article-outer {
        margin-bottom: 0px;
    }
    
    br[clear="all"] {
        display: none !important;
    }
    
    * {
        font-size: 12px !important;;
        font-weight: normal !important;;
    }
}
