css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document
url-prefix("http://www.google.com/calendar/"),
url-prefix("https://www.google.com/calendar/"),
domain("calendar.google.com")
{
	.applogo,
	.date-nav-today,
	.date-nav-prev,
	.date-nav-next,
	#topRightNavigation,
	#vr-header,
	#sidebar,
	#navfooter,
	#gb,
	.domainlogoparent,
	#vr-proto-nav,
	rule_to_hide
	{
		display: none !important;
	}

	/* Hide the tools in the palette */
	#mothertable > #nav {
		z-index: 2;
		-moz-transition-duration: .2s;
		position: fixed !important;
		width: 200px !important;
		height: 100% !important;
		padding: 30px !important;
		background-color: #FFF !important;
		opacity: .2 !important;
		left: -282px !important;
		border-right: #D44836 10px solid !important;
		margin-left: 20px !important;
	}

	/* Show the tools in the palette on hover */
	#mothertable > #nav:hover {
		-moz-transition-duration: .2s;
		position: fixed !important;
		z-index: 2;
		opacity: 1 !important;
		box-shadow: 4px 4px 6px rgba(0,0,0, 0.6) !important;
		left: -30px !important;
	}
	
	div#mothertable > div#maincell > div#mainbody {
		margin: 0 !important;
	}
	
	#mvEventContainer {
		margin-top : -30px !important;
		padding-right : 20px !important;
	}

	/* move header in bottom */
	#vr-nav {
		z-index : 10000;
		position: absolute !important;
		left: 10px !important;
		bottom: 0px !important;
		width : auto !important;
		background-color: rgba(0,0,0,0) !important;
	}
	
	#mainnav {
		height : 20px !important;
		background-color: rgba(0,0,0,0) !important;
	}
	#topLeftNavigation {
		position: absolute !important;
		bottom: 10px !important;
		left: 0px !important;
	}
	
	#dateBox\:2 {
		padding : 0px !important;
	}
	
	#nt1 {
		position: absolute !important;
		bottom : 10px !important;
		left : -10px !important;
	}

	.st-dtitle {
		padding : 0px 10px !important;
	}

	.st-c-pos {
		height: 1.5em !important;;
	} 
	
	.rb-n {
		height: 1.3em !important;;
	}


	#rhstogglecell {
		right: 0px;
	}
	
	#gadgetcell {
		width: 180px !important;
		margin-right: 0px !important;
	}

	.bubble {
		margin: auto 50px auto 50px !important;
	}

	.st-dtitle:last-child,
	.st-dtitle:nth-last-child(2)
	{
		background-color: #F5C7D6 !important;
	}

	.st-dtitle-today, .wk-today {
		/* Highlight today */
		background-color: rgba(77,144,254,.8) !important;
		border: none !important;
		color: #FFF !important;
		font-weight: bold !important;
	}

	.st-bg-today, .tg-today, .bg-exists .tg-today, .st-bg-today {
		/* Highlight today (box) */
		background-color: rgba(77,144,254,0.1) !important;
		border: 1px solid rgba(77,144,254,0.7) !important;
	}

}
