css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("news.ycombinator.com") {
  * {background-color: #000000 !important; color: #cccccc !important; font-family: monospace !important; font-size: 10pt !important;}
  
  #hnmain {border: 1px solid #00ff00}
  
  #hnmain td[bgcolor] {border: 1px solid #00ff00; border-top-style: none; border-left-style: none; border-right-style: none}
  img {border-color: #00ff00 !important;}
  .pagetop > b > * {color: #ffffff !important;}
  
  .pagetop {color: #828282}
  .pagetop img ~ a:link, a:visited, a:hover, a:active {color: #50f148 !important;}
  .pagetop img ~ a:hover {text-decoration: underline}
  .pagetop a:link, a:visited, a:hover, a:active {color: #ffffff !important;}
  .pagetop a:hover {text-decoration: underline}
  
  .topsel a:link, a:visited, a:active {color: #ffff00 !important; font-weight: bold}
  
  .rank {color: #00ff00 !important;}
  .title a:link {color: #50f148 !important;}
  .title a:visited {color: #ad4ad2 !important;}
  .title a:hover {text-decoration: underline}
  .title a:active {color: #ffffff !important;}
  .sitebit {color: #a1ac25 !important;}
  .subtext {font-family: Verdana, Geneva, sans-serif !important; font-size: 7pt !important;}
  .subtext * {font-family: Verdana, Geneva, sans-serif !important; font-size: 7pt !important;}
  .subtext a:link, a:visited, a:hover, a:active {color: #cccccc !important;}
  
  .yclinks {color: #828282}
  .yclinks a:link, a:visited, a:hover, a:active {color: #ffffff}
  .yclinks a:hover {text-decoration: underline}
  
  textarea, input {color: #cccccc}
  .comhead * {font-family: Verdana, Geneva, sans-serif; font-size: 8pt}
  .comhead a:link, a:visited {color: #cccccc !important;}
  .comhead a:hover {text-decoration: underline}
  .comment * {font-size: 9.5pt}
  
  .reply * {font-family: Verdana, Geneva, sans-serif !important; font-size: 8pt}
  
  td {color: #cccccc; font-family: monospace}
  td[bgcolor] {font-size: 9.5pt}
  td a:link, a:visited, a:hover, a:active {color: #ffffff !important;}
  td a:hover {text-decoration: underline}
  
  b {color: #cccccc}
  form + a:link, a:visited, a:active {color: #50f148}
  a > span {color: #ffffff !important;}
  .admin td {font-size: 9.5pt}
}
