css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lazyfoo.net") {
  *{
    background-color: #121010 !important;
    color: white !important;
  }
  a {
    background-color: transparent;
    text-decoration: none !important;
  }
  .tutCode, .tutPreface, .tutText, .tutFooter {
    border: 0px !important;
  }
  .nav {
    text-align: center !important;
  }
  a.nav {
    text-align: center !important;
    display: inline-block;
    min-width: 10em !important;
  }
  a.nav, .tutCode, .tutFooter{
    border: solid #483B40 1px;
    border-radius: 5px;
    background-color: #241A20 !important;
    padding: 4px;
    font-weight: bold;
  }
  a.nav:hover{
    background: #483B40 !important;
  }
  .tutFooter {
    font-weight: initial;
  }
  .tutFooter a {
    background: transparent !important;
    font-weight: bold;
  }
  
  .tutCode {
    border-radius: 6px;
    padding: 6px;
    /*margin: 5px;*/
  }
}
