css:
@-moz-document domain(example.com) {
  .oHeader .oTwizzlr .oLogo {
      height: 62px;
      width: 62px;
      top: 2px;
      background: url(http://www.kutsevalov.name/wp-content/uploads/2015/05/Homer.png) center center no-repeat;
      background-size: 62px 62px;
  }
  .oHeader .oTwizzlr .oNavTab.isCurrent {
      color: #000 !important;
      border-bottom: 2px solid #000;
      padding-bottom: 0px !important;
  }
  .oHeader .oTwizzlr .oNavCondensed .oNavTab {
    padding-top: 26px !important;
    padding-bottom: 2px;
    margin-bottom: 24px !important;
    font-size: 14px !important;
  }
  body a {
     color: #02B1D0 !important;  
     text-decoration: underline;
  }
  body .oBtn {
      text-decoration: none;
  }
  .oBtnPrimary, .oBtnVisitorPrimary {
    text-decoration: none;
    background: #000;
    border-radius: 3px;
  }
  .oBtnPrimary:hover, .oBtnVisitorPrimary:hover, .oInputFileWrapper:hover~.oBtnPrimary, .oInputFileWrapper:hover~.oBtnVisitorPrimary {
      background: #444;
  }
  .oDateSelector .oDatePicker:hover {
      color: #444;    
  }
  .oMsgWarning, .oMsgLegacyWarning, .oMsgWarning:before, .oMsgLegacyWarning:before {
      border-color: #CB6114;
  }
  .oMsgWarning:before, .oMsgLegacyWarning:before {
      background-color: #CB6114;
  }
  .oBtnGrp>input:checked+label, .oBtnGrp>input:focus+label {
      background: #555;
  }
  .oLoading, .oIndicatorMsg {
      border: 2px solid #CB6114;
      color: #CB6114;
  }
}
