@-moz-document url("http://www.nextgenupdate.com/forums/infernoshout.php?do=detach") {
/**** main *****/
html
{
	background-image:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAM0lEQVQI1zXKsQ0AMQjAQCf6BgmxBfvv5obyU9H6fLr7V6kqVC5ARLDxAswMmYnKt7LnA+lSE2PlUFYpAAAAAElFTkSuQmCC) !important;
	background-attachment:fixed;
	background-position:center top;
}

/* line below active users */
#nguheader .nav-tabs
{
    border-bottom: 1px solid rgba(128,128,128,0.5) !important;
}

/* shout padding */
div [style="padding-top: 1px; padding-bottom: 1px;"]
{
	padding-top: 3px !important;
	padding-bottom: 3px !important;
}

/* shoutbox smilies and editshout bg */
#shoutbox_editshout, #shoutbox_smilies, #shoutbox_opts
{
	background-color: rgba(255,255,255,0.15) !important;
    margin-left: 0 !important;
    margin-right: 0 !important;
    padding-left: 10px !important;
    padding-right: 10px !important;
}

/* shout time */
.time
{
	color: rgba(136, 136, 136, 1.0) !important;
	font-size: 75%;
}

#sb_body
{
	color: rgba(136, 136, 136, 1.0);
	background-color: transparent !important;
	text-shadow: 1px 1px 2px rgba(0,0,0,0.51), -1px 1px 2px rgba(0,0,0,0.51), -1px -1px 2px rgba(0,0,0,0.51), 1px -1px 2px rgba(0,0,0,0.51);
    /* for firefox */
	width: 100% !important;
}

/* push scrollbar away from the border a bit */
.shoutbox_body_cont
{
    margin-right: 10px !important;
}

#nguheader .shoutbox_head
{
	background-image: none !important;
	background: -moz-linear-gradient(top,  rgba(255,255,255,0.15) 0%, rgba(255,255,255,0.32) 51%, rgba(255,255,255,0.15) 100%) !important;
	background: linear-gradient(to bottom,  rgba(255,255,255,0.15) 0%,rgba(255,255,255,0.32) 51%,rgba(255,255,255,0.15) 100%) !important;
}

/* highlight (used in notice) */
.highlight
{
	font-weight: bold !important;
	color: rgba(255, 255, 255, 0.8) !important;
	background-color: rgba(0, 136, 255, 0.8) !important;
	border-radius: 4px 4px 4px 4px !important;
	padding-left: 3px !important;
	padding-right: 3px !important;
}

/* text input boxes */
.shoutbox_edit_textarea, .shoutbox_editor
{
	background-color: rgba(0,0,0,0.7) !important;
    color: #c3c3c3 !important;
	border: none !important;
}

/*.shoutbox_editor
{
    border-radius: 15px 0px 0px 15px !important;
}

.shoutbox_editor_send
{
    border-radius: 15px 15px 15px 15px !important;
    left: -15px !important;
    z-index: 1337 !important;
}*/

/* buttons */
.shoutbox_editor_send, .btn-sm, .shoutbox_options, .shoutbox_select
{
	background-color: rgba(15,15,15,0.7) !important;
    color: #c3c3c3 !important;
	border: none !important;
	line-height: 20px !important;
}

/* buttons (hover) */
.shoutbox_editor_send:hover, .btn-sm:hover, .shoutbox_options:hover, .shoutbox_select:hover
{
	background-color: rgba(30,30,30,0.7) !important;
}

/* char counter */
#text_limit
{
    background-color: rgba(15,15,15,0.7) !important;
    color: #c3c3c3 !important;
	border: none !important;
}

/* text style dropdown buttons */
.shoutbox_select option
{
	background-color: rgba(0,0,0,0.6) !important;
}

/* change black within text style dropdown to be readable */
.shoutbox_select option[value="Black"]
{
	color:#c3c3c3 !important;
}

/* options dropdown menu */
.shoutbox_main .dropdown-menu
{
    background-color: rgba(0,0,0,0.8) !important;
}

/* dropdown menu arrow */
.shoutbox_main .dropdown-menu:before, .shoutbox_main .dropdown-menu:after
{
	border-bottom: 6px solid rgba(0,0,0,0.8) !important;
}

.shoutbox_main .dropdown-menu>li>a
{
    color:#c3c3c3 !important;
}

.shoutbox_main .dropdown-menu>li>a:hover
{
    background-color: rgba(64,64,64,0.8) !important;
}

.bootstrap .bootstrap-navbar .dropdown-menu li a:hover
{
    background-color: rgba(64,64,64,0.8) !important;
}
}

