{
  -webkit-filter: brightness(150%);
  -moz-filter: brightness(150%);
  -o-filter: brightness(150%);
  -ms-filter: brightness(150%);
  filter: brightness(150%);
}
#sbm_contextmenu
{
    text-shadow: 1px 1px 2px rgba(0,0,0,0.51), -1px 1px 2px rgba(0,0,0,0.51), -1px -1px 2px rgba(0,0,0,0.51), 1px -1px 2px rgba(0,0,0,0.51);
}
}

@-moz-document url("http://www.nextgenupdate.com/forums/infernoshout.php?do=detach") {
/* scrollbars */

.shoutbox_body_cont::-webkit-scrollbar
{
  width: 12px !important;  /* for vertical scrollbars */
  height: 12px !important; /* for horizontal scrollbars */
}

.shoutbox_body_cont::-webkit-scrollbar-track
{
  background: rgba(0, 0, 0, 0.1) !important;
}

.shoutbox_body_cont::-webkit-scrollbar-thumb
{
    border-radius: 10px !important;
  border: 1px solid rgba(255,255,255,0.3) !important;
}

.shoutbox_body_cont::-webkit-scrollbar-thumb:hover
{
  border: 1px solid rgba(255,255,255,0.5) !important;
}
}
