css:
@-moz-document url-prefix("http://watchseries.lt"),
               url-prefix("https://watchseries.lt")
  {
    
    body {
      background-color: #131313;
    }
    
    .centeres {
      width: 900px;
    }
    
    #header, #menu, #menu-top, ul#topnav, .bk-grey-long, .footer, .center_page, .bk-blue, #content2, #bann768x90 {
      width: 100%;
    }
    
    .series {
      color: #F0F0F0;
    }
    
    .search-box input[type="text"] {
      background-color: #232323;
    }
    
    .search-box, #header-3 {
      width: unset;
    }
    
    #header-3 {
      float: right;
    }
    
    #autocomplete {
      background-color: transparent;
    }
    
    #header-4 {
      display: none;
    }
    
    ul#topnav {
      color: transparent;
    }
    
    #topnav li:hover > a {
      background-color: #999;
    }
    
    div.brief {
      display: none;
    }
    
    .sp-sidetower {
      display: none;
    }
    
    .episode-wrap1 {
      width: 100%;
      background-image: none;
    }
    
    .bg-casing-top{
      background-color: #001221;
      background-color: #0C0C0D;
      background-color: #131313;
      background-image: linear-gradient(to bottom, #232323, rgba(0, 0, 0, 0));
    }
    .bk-grey-long>div:not(.bg-tr):first-child{
      background-color: #131313 !important;
      background-image: linear-gradient(to bottom, #232323, rgba(0, 0, 0, 0)) !important;
      background-clip: content-box !important;
      /*padding-bottom: 0 !important;*/
      padding-top: 15px !important;
      text-align: center;
    }
    
    .bk-grey-long>div:not(.bg-tr):first-child>div {
      margin-left: unset !important;
    }
    
    .bg-tr {
      padding: 15px 0;
    }
    
    .bg-casing-tr {
      color: #999;
    }
    
    .listings li {
      border-bottom-color: #333;
    }
    
    .listings li a {
      color: #fff;
    }
    
    .epnum, .front-sum {
      color: #2CBCFF;
    }
    
    h2.lists a {
      color: #278AB8;
    }
    
    h1.channel-title {
      color: #999;
    }
    
    .myTable {
      background: rgba(0, 0, 0, 0.1) linear-gradient(to right, rgba(0, 0, 0, 0.5) 0px, rgba(0, 0, 0, 0.05) 50%) no-repeat scroll 0% 0%;
      width: 100%;
    }
    
    .myTable span {
      color: #F0F0F0;
    }
    
    .myTable .buttonlink, a.npbutton, a:visited.npbutton, .pagination a {
      background: #232323 none repeat scroll 0% 0%;
      border-radius: 5px;
      color: #2CBCFF;
    }
    
    .myTable tr:hover a.buttonlink {
      color: #2CBCFF;
    }
    
    .myTable tr:hover a:hover.buttonlink, a:hover.npbutton {
      background: #2CBCFF none repeat scroll 0% 0%;
      color: #232323;
    }
    
    .freeEpisode {
      display: none;
    }
    
    .bg-casing-tr, .bg-casing-tl, .item-summary-tl, .bg-casing-br, .bg-casing-br-item, .bg-casing-bl, .bg-tr {
      background-image: none;
    }
    
    .footer {
      background-color: #232323;
      margin-bottom: 0;
    }
    #sticky-footer {
      display: none;
    }
    
  }
