body {
    background: none repeat scroll 0% 0% black;
    background-color: black;
    background-repeat: no-repeat;
    padding-bottom: 20px;
}

.sheet {

    border-color: black;
    box-shadow: 0px 0px 10px rgba(0, 61, 234, 0.5);
}
    .forum-title a {
    color: black;
    }
.form-post {
    margin-top: 10px;
    border: 1px solid black;
    border-radius: 1px 8px 1px 5px;
}

.form-post .titre-bloc {
    background: none repeat scroll 0% 0% black;
}
    
    .pages .active {
    background: none repeat scroll 0% 0% black;
    color: #FFF;
    font-weight: bold;
}

.liste-topics .title {
    padding-top: 5px;
}

.message .edit-mention {
    color:#f60
}

.aside .menu .title {
    background:black;
    border-bottom:1px solid #444444;
}

.aside .menu .menu-content a, .aside .menu .menu-content span {
	border-bottom:1px solid #444444;
}

.aside .menu .menu-content {
	background:black;
}

.aside .menu .menu-content a, .aside .menu .menu-content span {
    color:#f60;
}
}

