css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
  
domain("www.teamliquid.net"),
domain("www.liquiddota.com"),
domain("www.liquidhearth.com")
/*domain("www.liquidlegends.net")*/
{
  body, tr.odd, .registration_form
  {
    background: #1C1C1C;
    color: #FAFAFA;
  }
  
  div[style]
  {
    background: transparent !important;
  }
  
  .calhead button, .calhead select
  {
    filter: invert(100%);
    background: #CCC;
  }
  
  #news_column_left
  {
    border: none;
    background: #0A0A0A;
  }
  
  #main-content, .sidebar_block .sbcontents, td.sidemenu, #fp_wrap #fp_content, td.lichtb, .ev-btn:hover, #main-content-wide, #evcal.viewwk .ev-block:hover, tr.even, .rsb-block
  {
    background: #0A0A0A;
  }
  
  td.donkerb, .fp1 > section > article, #news_poll, #news_spotlight, td.sidemenu .ev-live, .forum-index, #evcal.viewwk .ev-block, #evcal td
  {
    background-color: #1C1C1C;
  }
  
  .sidebar_block .sbcontents a:hover, .sidebar_block .sbcontents a:visited ,.sidebar_block .sbcontents a:link, a.rightmenu:hover, a.rightmenu:link, a.rightmenu:visited, a.nounderline:hover, a.nounderline:link, a.nounderline:visited, a:hover, a:link, a:visited, a, #news_community ul li h4.thread_title a:hover, #news_community ul li h4.thread_title a:link, #news_community ul li h4.thread_title a:visited, #news_spotlight h4.thread_title a, #news_spotlight h4.thread_title a:visited, #news_spotlight h4.thread_title a:hover
  {
      color: #0973BF;
  }
  
  .sidebar_block .sbcontents .sbtitle a:link, .sidebar_block .sbcontents .sbtitle a:visited, .sidebar_block .sbcontents .sbtitle a:hover, td
  {
    color: #CCCCCC;
    padding-bottom: 5px;
  }
  
  .forumsig, .quote, #evcal td
  {
    color: #CCCCCC;
  }
  
  p, #navwrapper, a.block_header, .submessage, .rightmenu, .forumindex, .forummsginfo, a.block_header, .forummsg, .submessage label, .ev-feed, #article .text
  {
    color: #FAFAFA !important;
  }
  
  .fp_box_content, #news_community, .lightback, .featurebox:hover, .featurebox
  {
    background: #1C1C1C;
    border: solid white 1px;
  }
  
  td.titelbalk, div#spoiler_1, div#spoiler_2, div#spoiler_3, div#spoiler_4, div#spoiler_5, div#spoiler_6, div#spoiler_7, div#spoiler_8, div#spoiler_9, div#spoiler_10, div#spoiler_11, div#spoiler_12, div#spoiler_13, div#spoiler_14, div#spoiler_15, div#spoiler_16, div#spoiler_17, div#spoiler_18, div#spoiler_19, div#spoiler_20, div#spoiler_21, div#spoiler_22, div#spoiler_23, div#spoiler_24, div#spoiler_25, div#spoiler_26, div#spoiler_27, div#spoiler_28, div#spoiler_29, div#spoiler_30
  {
    background-color: #0A0A0A !important;
  }
  
  .sidebar_block .block-news_left_mid a.comments, span.viewers
  {
    text-shadow: none;
  }
  
  a[href]
  {
    text-shadow: none !important;
  }
  
  .regerror
  {
    background: #B44335;
  }
  
  #TLStrategybody, .solid footer, .pagination a:hover
  {
    background: #0A0A0A;
    color: #FAFAFA;
  }
  
  #searchicon, .forumPost, .pagination a, #Newsintro
  {
    background: #1C1C1C;
    color: #FAFAFA;
  }
  
  td[style="background:#fff;padding:20px;padding-top:20px;padding-bottom:20px;text-align:center;font-size:16px"]
  {
    border: solid white 1px;
    background: #1C1C1C !important;
  }
}

@-moz-document 
  
url-prefix("http://www.teamliquid.net/mytlnet/")
{
  td[style], TD.btn_down
  {
    background-color: #0A0A0A !important;
    border: solid white 1px !important;
  }
  
  TR.pm_boxhead
  {
    background: #1C1C1C;
  }
  
  TD.pm_boxtop, td, .srch_hd
  {
    background: #254673;
    border: solid white 1px;
  }
}

@-moz-document
url-prefix("http://wiki.teamliquid.net")
{
  body, #main-content-column, #main-content
  {
    background: #1C1C1C !important;
    color: #FAFAFA !important;
  }
  
  #sidebar-toc-column, #wrap
  {
    background: #131313 !important;
  }
  
  #sidebar-toc ul li.active > a, #sidebar-toc ul li > a:hover, #sidebar-toc ul li > a:focus
  {
    background: #0A0A0A !important;
  }
  
  div.thumbinner, html .thumbcaption
  {
    background: #131313 !important;
  }
  
  caption
  {
    color: #FAFAFA !important;
  }
  
  td[style="background:#f0f0f0;"], .wikitable > tbody > tr > th
  {
    background: #131313 !important;
  }
  
  #footer, #footer > *
  {
    background: #131313 !important;
  }
}
