@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("spaces.ru") {
/* #04.06.15 0.01v Early Alpha build has realeased*/
  
  

  
  /* Глобальные вещи */
  html {  
     background: fixed url( /*[[wallpapper]]*/ ) no-repeat;
     background-size: cover;    
     background-color: rgba(256,256,256, 0.5)!important; 
     left: /*[[pos]]*/
    }

  
  #wrap_all {width: 60%!important; position: absolute;}
  
  
  body {
    background:  url("http://") no-repeat!important;
  }
  
  
  .stnd_padd5:after {
    content: "by 7KiLL";
    position: absolute!important;
    text-align: center!important;
    left: 90%;
    opacity: 0.5;
    font-style:italic;
  }
  
  
   .moders_header_block, #photos_slider_main, #b-title b-title_first, .b-title__link[href^="http://spaces.ru/shared_zone/?dir=1690"], .b-title__link[href^="http://spaces.ru/shared_zone/?dir=4"], .photos_slider_main .carousel-screen, #navi_footer_wrap, .footer__links-wrapper, #scroll_page {display: none!important;}
  
  
  /* Прозрачность */
  
  #wrap_all {background:  rgba(256,256,256,0.7)!important;}  /* Каркас всего контента */  
  
   /* Убираем фон вообще */
     #index_page, #siteContent, .main, #main_content_block, #main_shadow, #main_content, .page_fixer, #content_wrap_move, .content_wrap,#left_nav_bg, .widgets-group, .clear *, form .stnd_padd.light_border_bottom {background:rgba(256,256,256,0)!important;}
  
  /* Прозрачность и hover */
  .content-item_first, .content-item, .content-user, .content-widget, .list_item.overfl_hid, .list_item.blue_wrap_block.lh_160, .links-group .list-link_arrow, .help-block, .btn-main, .content-bl.break-word.content-bl__sep.text_anketa {background: rgba(256,256,256, 0.75)!important;}
  .content-item_first:hover, .content-item:hover, .content-user:hover, .content-widget:hover, .list_item.overfl_hid:hover, .list_item.stnd_padd.oh.lenta_item.round_corners:hover, .list-link.list-link_arrow:hover, .help-block:hover, .btn-main:hover, .content-bl.break-word.content-bl__sep.text_anketa:hover { background: rgba(256,256,256, 1)!important;}
  
  .lenta_item {background: rgba(256,256,256,0.5);}
  .lenta_item:hover {background: rgba(256,256,256,1);}
  
  .tile-menu__link {opacity: 0.7;}
  .tile-menu__link:hover {-moz-transform: rotate(-2deg); opacity: 1;}
  
  /* Другие настройки прозрачности */
  .content-bl__sep, .content-bl__sep2, .text_anketa_sep {background: rgba(256,256,256, 0.89)!important;}
  .main_switcher {opacity: 0.85;}
  .blog-item__subject, .light_border_bottom {background: rgba(256,256,256,0.5);}
  
  
 
  /* ТЕСТИМ ЗДЕСЬ */
  .file_comments_info.list_item.oh {background: rgba(256,256,256,0.4)!important;}
  .stnd_padd.light_border_bottom {background: rgba(256,256,256,0.4)!important;}
  .stnd_padd.light_border_bottom:hover {background: rgba(256,256,256,0.7)!important;}
  
  .list_item.blue_wrap_block {background: rgba(256,256,256,0.7)!important;}
  #textarea {opacity: 0.6!important;}
  #textarea:hover {opacity: 1!important;}

  /* Цитаты */
  .friends_access_list {background: rgba(192,192,192, 0.8)!important;}
  .friends_access_list:hover {background: rgba(192,192,192, 1)!important;}
  .list_item.oh:hover {background: rgba(256,256,256,0.6)!important;}
  
  
  
  /* Блог */
  .blog-item_short * {background: rgba(256,256,256,0)!important;}
  .list-link.blog-item_short:hover {background: rgba(256,256,256, 1)!important;}
  .list_item {background: rgba(256,256,256, 0.25)!important;}
  .list_item:hover {background: rgba(256,256,256, 0.7)!important;} 
  .list-link.blog-item_short  {background: rgba(256,256,256, 0)!important;}
 
  /*Зона обмена */
  .bottom_link_block.lh_160 {background: rgba(256,256,256, 0.4)!important;}
  .t-block_item.t-padd_right.light_border_bottom.t-bg3.t-link_no_underline_block:hover {background: rgba(256,256,256,0.8)!important;}
  
  .mt_big {margin-top: 0px!important;}
  .busi {display: none!important;}
  
  .link-return {background: rgba(256,256,256,0.7)!important;}
  
  .pgn {margin: 0px; background: rgba(256,256,256,0.6)!important;}
  .pgn__link.pgn__link_prev.pgn__link_disabled:hover, table__cell, table__cell.table__cell_last:hover {background: rgba(256,256,256,1)!important;}
  
  .list_item.bookmark_block {background: rgba(256,256,256,0)!important;}
  .list_item.bookmark_block:hover {background: rgba(256,256,256,0.8)!important;}
  .list_item.small_gradient.bookmark_block {border-bottom: 2px solid #B3B3B3;}
  
  /* Блоги на главной */
  .widgets-group {background: rgba(256,256,256,0)!important;}
  .no-break:before {background: rgba(256,256,256,0);}
  .list-link.blog-item_short {background: rgba(256,256,256,0.5)!important;}
  .list-link.blog-item_short.list-link_last, .blog-item__author.no-break, .left {color:black;}
  .blog-item__author.no-break, .blog-item__time {font-weight: bold;}
  .blog-toolbar .m:before{content: url(http://i.imgur.com/YklT4Av.png); position: relative; top:4px; right: 2px;}
  
  .left {background: rgba(256,256,256,0)!important;}
  .clear * {clear: none!important;}
  .list-link {background: rgba(256,256,256, 0.4)!important;}
  .list-link:hover {background: rgba(256,256,256,1)!important;}
  
  
  /* Поиск внизу на главной */
  .widgets-wrap.hide-suggest__list {display: none;}
  
  
  #contacts_wrapper {background: rgba(256,256,256,0.1)!important;}
  
  
  /* Почта */
  .m_message_i-old {background: rgba(256,256,256,0.5)!important;}
  .m_message_i-old:hover {background: rgba(256,256,256,0.8)!important;}
  
  .message_list_item.stnd_padd2.t-bg3.light_border_bottom.oh:hover {background: rgba(256,256,256,0.85)!important;}
  


   #left_nav ul li.separate_item{
	border-top:1px solid black;
}
  
  .bt0 {border-top:0!important;}
  .bb0 {border-bottom:0!important;}
  #left_nav {background: #32394A none repeat scroll 0% 0% !important ;}
  #left_nav ul li {border-bottom: 1px solid #242A37!important; border-top: 0px!important;}
  #left_nav ul li a:hover {background:#434b5a;}
  .s_title {background:#434B5A none repeat scroll 0% 0%!important;}   
  .s_title {background:#434B5A none repeat scroll 0% 0%!important;} 
  .list-link.blog-item_short.blog-item_lenta {background: rgba(256,256,256,0)!important;}
  
  
 /* html #gview_picture_1, html .gview_link {opacity: 10!important;}*/
  
