css:
@-moz-document domain(egloos.com), domain(egloos.zum.com) {
  * {
      color: black!important;
      font-family: "이롭게 바탕체", "KoPubBatang", "나눔명조", "맑은 고딕", "굴림", "바탕" !important; 
  }

  .post_view a {
      color: blue !important;
      border-bottom: none !important;
  }

  .post_view .post_title h2 a {
      border-bottom: 1px none !important;
  }

  .aside,
  #section_sidebar1,
  #section_sidebar2,
  #section_sidebar3,
  #section_sidebar4,
  #section_sidebar5
  {
      margin: 0;
      display:  none !important;
      float: none ;
      width: 0 !important;
      background-color: transparent !important;
  }

  .content {
      padding: 0;
  }

  .content_wrap {
      width: 100%;
      padding: 0px;
  }

  td, div, li {
      max-width: 700px;
  }

  body, td, div, li {
      background-image: none!important;
      background-color: white!important;
      color: black;
      font-size: medium;
      line-height: 1.9em!important;
      text-align: justify;
      word-break: keep-all;
      border-left: none!important;
      border-right: none!important;
  }

  body {
      background-color: #F0F0F0 !important;
  }

  .header, .url {
      text-align: center;
      border-bottom: none;
      height: auto;
  }

  .topmenu, .widemenu {
      display: none;
  }

  .body {
      margin: auto;
      padding: 50px;
      max-width: 780px;
  }

  .post_view {
      font-size: medium;
      line-height: 1.9em;
      background-color: white;
      border: none;
      border-bottom: 1px solid #999;
  }

  .post_view .post_title h2 {
      font-size: x-large;
      line-height: 2.1em;
      border-bottom: 1px dotted #999;
      text-align: start;
  }

  font, div, div span, div p {
      font-size: medium!important;
      line-height: 1.9em!important;
  }

  hr.line {
      border: 1px none;
  }
  
  img {
      max-width:100%;
      height: auto;
  }
}
