css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(namu.wiki) {
  body, th, td, input, select, textarea, button, div, p, li {
      font-size: 1em !important;
      line-height: 1.8em;
      word-break: keep-all !important;
  }
  
  @font-face {
    font-family:'RIDIBatang';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_twelve@1.0/RIDIBatang.woff') format('woff');
    font-weight: normal;
    font-style: normal;
  }
  
  body {
      background-color: white !important;
  }

  .container-fluid {
      padding: 7em !important;
      border: none !important;
  }

  body, th, td, input, select, textarea, button, div, p, li, h1, h2, h3, h4, h5, h6 {
      font-family:'RIDIBatang' !important;
  }

 .wiki-closure > tbody > tr > td {
    padding: 1.2em;
  }

  /* footnote */

  .footnote-list {
      position: relative;
      margin: 10px 0;
      padding-left: 3em;
      font-size: 0.9em;
      line-height: 1.4em;
  }

  .footnote-list a[href*="rfn"] {
      position: absolute;
      display: block;
      width: 140px;
      left: -105px;
      text-align: right;
  }
}
