css:
@-moz-document domain("developpez.com"), domain("developpez.net") {
@media screen {
    
    body {
       font-family: "Roboto","Lato","Open Sans",Arial, sans-serif;                                     
   }
  
  .layout, #layout {
    margin: auto 0!important;
  }  

  h2.alauneType, h1.alauneType {
    background: #0099ee!important;
  }
    
  #quicknav {
    float:right!important;
  }   

  #gabarit_tabmenu,
  #gabarit_menu{
    background: #0066aa!important;
    border-radius: 0!important;
    border-bottom: 1px solid #5a7f97;
  }
  
  #gabarit_tabmenu #tabs > li > a.selected,
  #gabarit_tabmenu #tabs > li > a.selected:hover,
  #gabarit_tabmenu #tabs > li:hover > a {
    background: #ccccff!important;
  }
  
  #gabarit_tabmenu #tabs > li {
    background: none!important;
    line-height: 28px!important;
    height: 28px!important;
  }
  
  #gabarit_tabmenu #tabs {
    background: none!important;
  }
  
  #gabarit_tabmenu #tabs li a:hover {
    background: #ddddff!important;
  }
  #gabarit_tabmenu #tabs li .submenu li a:hover {
    color: inherit!important;
    background: none!important;
  }
  
  #gabarit_tabmenu #tabs li:hover a+ul.submenu,
  #gabarit_tabmenu #tabs li:hover ul.submenu {
    top: 26px!important;
  }
  
  #gabarit_tabmenu div,
  #gabarit_topicmenu div {
    display:none!important;
  }
  
  #shadow1, #shadow2 {
    background: transparent!important;
    padding: 0!important;
  }
  #gabarit_resourcemenu {
    background: #0066aa!important;
    height: auto!important;
  }
  #gabarit_resourcemenu_left, #gabarit_resourcemenu_right {
    background: none!important;
  }
  #gabarit_resourcemenu #resources {
    padding-bottom: 2px!important;
  }
  #gabarit_resourcemenu #resources li {
    background: #0099ee!important;
    height: auto!important;
    padding-top: 4px!important;
  }
  #gabarit_resourcemenu #resources li:hover {
    background: #ccccff!important;
  }
  
  #gabarit_footer,
  #gabarit_footer #gabarit_footer_right,
  #gabarit_footer #gabarit_footer_left {
    background: none!important;
  }
  
  #gabarit_topicmenu {
    background: #0066aa!important;
  }
  
  #gabarit_topicmenu #topics {
    background: none!important;
  }
  
  #gabarit_topicmenu #topics li span {
    background: #0099ee!important;  
  }
      
  #gabarit_topicmenu #topics li.selected span,
  #gabarit_topicmenu #topics li span:hover {
    background: #ccccff!important;    
  }
  
  div.contenuPage {
    padding: 0!important;
  }
  
  .above_body {
    margin: 0!important;
    background: #0066aa!important;
    border-radius: 0!important;
  }
  
  .above_threadlist {
    height: auto;
    margin-top: 0;
  }
  
  .navtabs {
    background: none!important;
  }
  .navtabs ul {
    border-top: none!important;
  }
  .globalsearch .textboxcontainer,
  .globalsearch .textboxcontainer span,
  .globalsearch .buttoncontainer,
  .globalsearch .buttoncontainer span {
    border: none!important;
  }
  .globalsearch input.textbox {
    font-size: 13px!important;
  }

  .forumhead,
  .threadlisthead {
    background: #0099ee!important;
  }
  
  .forumhead span.forumthreadpost,
  .forumbit_nopost .forumhead span.forumthreadpost {
    font-size: 10px!important;
  }

  .forumbit_post .foruminfo {
    min-height: 32px!important;
  }

  .forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow {
    min-height: 40px!important;
  }
      
  .wgo_block {
    margin: 0!important;
    box-shadow: none!important;
  }
      
  .wgo_block .blockhead {
    background: #0066aa!important;    
  }

  .below_threadlist .threadpagenav {
    padding-bottom: 0!important;
  }

  #gabarit_pied {
    background: #0066aa!important;
    height: auto!important;
  }

  #gabarit_pied #gabarit_pied_contenu .gabarit_pied_div {
    display:inline-block!important;
    float:none!important;
  }

  #gabarit_pied #gabarit_pied_left,
  #gabarit_pied #gabarit_pied_right {
    background: none!important;
    height: auto!important;
  }

  #gabarit_pied #gabarit_pied_contenu .gabarit_pied_div {
    width: 240px!important;
  }

  #gabarit_pied #gabarit_pied_contenu .gabarit_pied_div_separation {
    display:none!important;
  }
  #gabarit_pied #gabarit_pied_copyright p {
    margin: 5px 0 0 0!important;
    padding-bottom: 5px!important;
  }
  
  .footer {
    margin-top: 0!important;
    background: transparent!important;
    border: none!important;
    box-shadow: none!important;
  }

  .footer a,
  .footer a:hover {
    color: rgb(50, 80, 120)!important;
  }

  .footer_time {
    padding-top: 0!important;
  }

  .footer_copyright, .footer_morecopyright {
    margin-top: 0!important;
  }
  
  .footer_links {
    margin-top: 0!important;
  }

  .blockhead,
  .forum_info .blockhead {
    background: #0066aa!important;
  }

  .threadbit .alt {
    background: none!important;
    margin-top: 8px!important;
  }

  .threadbit .nonsticky {
    background: #ffffff url(http://www.developpez.net/forums/images/gradients/grey-up.png) repeat-x left bottom!important;
  }

  .memberprofiletabunder {
    height: 1px!important;
  }
  
  .toolsmenu {
    margin: 0!important;
    padding: 5px!important;
  }
    
  #pagetitle {
    padding: 5px 0!important;
  }
    
  .postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead {
    background: #0099ee!important;
  }
        
  .postbit .posttitle, .postbitlegacy .title {
    padding: 0!important;
  }

  .thread_info h4 {
    background: #0066aa!important;
  }
  
  #posting_rules, #forumrules  {
    font-size: 0.8em!important;
  }

  .formcontrols {
    border: none!important;
  }
  
  .formcontrols .blockrow {
    padding: 5px 0!important;
  }
  
  span.cke_skin_kama {
    border: none!important;
    padding: 0!important;
  }
      
  #nrreview .header {
    padding: 5px!important;
    font-size: 0.9em!important;
  }

  #actu,
  .ArticleCompletDroite{
    padding-left: 16px!important;
  }

  .voiraussiTitle,
  div.TitreCategorieCours{
    background: #0099ee!important;
    border-radius: 0!important;
  }

  .forumLink {
    background: #0099ee!important;
  }
  
  .postMessage {
    background: #e9e9e9!important;
  }

  .navlinks {
    width: auto!important;
    clear: none!important;
    float: right!important;
    background: none!important;
    border-top: none!important;
    margin: 0!important;
    padding: 3px 0!important;
  }
  
  h2.searchlisthead {
    background: #0099ee!important;
  }

  div.cadre {
    background: #f6f6f6!important;
    padding: 10px!important;
  }
    
  #gabarit_barre1 {  
      background: #0066aa!important;
  }
    
  #gabarit_barre2 {
    background: #004499!important;
  }
  
  #gabarit_barre1 li  ,
  #gabarit_barre2 li {    
    background-color: #6BC7FD;
    background: linear-gradient(to bottom, #6BC7FD 0%,#1181CA 100%);
  }
    
  #gabarit_barre2 ul a {
    font-size: 8pt;
  }

}
}
