html,
body {
    max-width: 100%;
    overflow-x: hidden;
}
#navbar {
    background-color: rgba(0, 0, 0, 0);
}
#navbar a {
    background-color: rgba(0, 0, 0, 0);
    border-bottom: 0px solid #0077c0;
    border-left: none;
