.left::-webkit-scrollbar {
    display: none;
}
a[accesskey="0"]:hover {
    text-overflow: ellipsis;
    max-height: 1.2em;
}
#navbar li a:hover {
    background-color: #01619E;
    border-bottom: 0px solid #b22222;
    border-left: none;
    border-right: 0px solid #b22222;
    border-top: 0px solid #b22222;
    color: #fff;
    font-weight: bold;
    width: 150px;
}
.leftbox ul {
    list-style-type: none;
    margin-left: 5px;
}
.leftbox h4 {
    display: none;
    border: none;
}
.leftbox a {
    color: white;
}
.leftbox {
    background-color: #01619E !important;
    padding-bottom: 10px;
    border: none !important;
}
.leftbox2 {
    padding-bottom: 100px;
}
.left {
    overflow: auto;
    position: fixed;
    height: 100%;
    padding-right: 13px;
    background-color: #01619E;
}
h2.forums a {
    color: white !important;
}
div.portlet ul,
div.bookmarks ul {
    margin-left: 163px;
    font-size: 20px;
    padding-top: 12px;
    font-weight: normal;
    background-color: #01619E;
    padding-bottom: 20px;
    padding-left: 5px;
}
.pagename {
    background-color: #01619E;
    margin-top: -45px;
    margin-left: -10px;
    color: white;
    font-weight: normal;
    padding-left: 25px;
    padding-bottom: 50px;
}
div.portlet ul {
    margin-top: 45px;
}
#mw-content-text {
    -webkit-animation: aniload 0.5s;
    -moz-animation: aniload 0.5s;
    -ms-animation: aniload 0.5s;
    -o-animation: aniload 0.5s;
    animation: aniload 0.5s;
    margin-right: 20px;
    border-radius: 2px;
    padding: 10px;
    background-color: #fafafa;
    margin-right: 330px;
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
}
table.infobox td {
    -moz-border-radius: 0px;
    -webkit-border-radius: 0px;
    border-radius: 0px;
}
#ca-edit a {
    font-weight: 100;
}
/* Step 1: Build the Animation */

@-webkit-keyframes aniload {
    from {
        -webkit-transform: translate(0px, 1000px)
    }
    to {
        -webkit-transform: translate(0px, 0px)
    }
}
@-moz-keyframes aniload {
    from {
        -moz-transform: translate(0px, 1000px)
    }
    to {
        -moz-transform: translate(0px, 0px)
    }
}
@-ms-keyframes aniload {
    from {
        -ms-transform: translate(0px, 1000px)
    }
    to {
        -ms-transform: translate(0px, 0px)
    }
}
@-o-keyframes aniload {
    from {
        -o-transform: translate(0px, 1000px)
    }
    to {
        -o-transform: translate(0px, 0px)
    }
}
@keyframes aniload {
    from {
        transform: translate(0px, 1000px)
    }
    to {
        transform: translate(0px, 0px)
    }
}
div.left {
    margin-top: 66px;
}
div.portlet li.selected a {
    background-color: transparent;
    border: 0px solid #fabd23;
    font-weight: 100;
}
#searchInput {
    display: block;
    border: none;
    color: white;
    background-image: url("data:image/png;base64,");
    background-color: rgba(0, 0, 0, 0);
    border-bottom: 1px solid white;
}
#searchInput::-webkit-input-placeholder {
    color: white;
}
.searchButton {
    display: none;
}
#searchGoButton{display:none;}
#searchInput:-moz-placeholder {
    /* Firefox 18- */
    
    color: white;
}
#searchInput::-moz-placeholder {
    /* Firefox 19+ */
    
    color: white;
}
#searchInput:-ms-input-placeholder {
    color: white;
}
.dropdown {
    display: inline-block;
    margin: 10px;
    position: relative;
    padding: 0;
    border: 0;
    border-radius: 5px;
    font-size: 24px;
    font-weight: 400;
    float: right;
    background: #01619E;
    color: #FFF;
}
.dropdown > input {
    display: none;
    position: fixed;
    z-index: -2;
}
.dropdown > label {
    display: inline-block;
    max-width: 100%;
    margin-bottom: 5px;
    color:white;
    font-weight: 700;
}
.drop-ttl {
    font-weight: 400;
    padding: 5px 20px;
}
.dropdown ul {
    width: 100%;
    font-weight: 400;
    margin: 0;
    padding: 0;
    position: absolute;
    top: 0;
    text-align: center;
    list-style-type: none;
    background: #FFF;
    color: #01619E;
    box-shadow: 0px 8px 17px 0px rgba(0, 0, 0, 0.2), 0px 6px 20px 0px rgba(0, 0, 0, 0.19);
    border-radius: 5px;
}
.dropdown ul >li {
    padding: 5px 20px;
}
.dropdown ul > li > a {
    color: inherit;
    outline: 0;
    text-decoration: none;
    cursor: pointer;
}
.dropdown ul > li:hover {
    background: #DEDEDE;
}
input[type="checkbox"]:checked ~ label > ul {
    display: none;
}
}
