css:
@-moz-document domain("yourdomain.atlassian.net") {
/* Top Bar */
#create_link{
	background-color: #5E9C39;
}
#create_link:after{
	content:'...';
}

/* Navigator Area */
.navigator-sidebar{
	border-right-color:#1E384A;
    background-color:#1E384A;
}
.navigator-container:not(.navigator-sidebar-collapsed) .navigator-sidebar:not(.collapsed):after{
	content:' ';
    position:absolute;
    top:0;
    right:0;
    bottom:0;
    width:10px;
    background: linear-gradient(to right, rgba(0,0,0,0) 0%, rgba(0,0,0,0.33) 100%);
}

.filter-panel-section{ 
    border-top-color:rgba(255,255,255,0.15); 
}
.filter-title{
	color:rgba(255,255,255,0.5);
}
filter-list > li:hover{
	background-color:rgba(255,255,255,0.25);
}
.filter-list > li filter-link{
	color:rgba(255,255,255,0.75);
}
.filter-list>li .filter-link:active, 
.filter-list>li .filter-link.active, 
.filter-list>li .filter-link:focus{
	color:#FFF;
}
.filter-list>li:hover .filter-link:active, 
.filter-list>li:hover .filter-link.active, 
.filter-list>li:hover .filter-link:focus{
    color:#3f7499 !important;
}

.filter-options .aui-button.aui-button-light, 
.filter-options a.aui-button.aui-button-light, 
.filter-options .aui-button:visited.aui-button-light{
	background:#EBF2F9;
    color:#1E384A;
    box-shadow:1px 1px 6px #000;
}

.aui-dropdown2.aui-style-default .active,
.aui-list a.active, .aui-list .aui-list-item.active>a{
	background-color:#3f7499;
}

/* Search Bar */
.saved-search-selector{
	border-bottom-color:RGBA(181, 190, 197, 1);
}
.navigator-search{
	background:RGBA(205, 215, 224, 1);
	border-bottom-color:RGBA(181, 190, 197, 1);
}

/* List Result Panel */
.details-layout .split-view .list-results-panel{
	position:relative;
}
.details-layout .split-view .list-results-panel:after{
	content:' ';
    position:absolute;
    top:0;
    right:0;
    bottom:0;
    width:10px;
    background: linear-gradient(to right, RGBA(30, 56, 74, 0) 0%, RGBA(30, 56, 74, 0.15) 100%);
}

.details-layout .split-view .list-results-panel .list-panel,
.details-layout .split-view .list-results-panel{
	background-color:#EBF2F9;
	padding:0;
}
.details-layout .split-view .list-results-panel .list-ordering{
	padding:10px 10px 10px 15px;
}

.details-layout .split-view .list-results-panel .list-panel .issue-list > li{
	padding:10px 10px 2px 20px;
}
.details-layout .split-view .list-results-panel .list-panel .issue-list>li.focused{
	position:relative;
	background-color:RGBA(255, 211, 81, 1);
}
.details-layout .split-view .list-results-panel .list-panel .issue-list>li.focused a{
	color:RGBA(117, 81, 30, 1);
}

.details-layout .split-view .list-results-panel .list-panel .issue-list>li a .issue-link-key{
	font-weight:bold;
}

/* Operation Bars (at top of task) */
#opsbar-edit-issue_container{}
#opsbar-edit-issue_container .aui-iconfont-edit:before{
	color:#FFF;
}
#opsbar-edit-issue_container li a{
    background: #22292F;
    border: 1px solid #000;
    color: #FFF;
    text-shadow: 0 1px 0 black;
}

#opsbar-comment-issue_container{}

#opsbar-opsbar-operations{}

#opsbar-opsbar-transitions li a{
    background: #3f7499;
    border: 1px solid #1E384A;
    color: #FFF;
    text-shadow: 0 1px 0 black;
}

#opsbar-opsbar-admin{}

/* Issue Editing */
#issue-content .issue-body-content{
	padding-left:0;
	padding-right:0;
	position:relative;
}
#issue-content .issue-body{
	border-top:1px solid RGBA(181, 190, 197, 1);
}
#issue-content .issue-main-column,
#issue-content .issue-side-column{
	padding-top:20px;
}
#issue-content .issue-main-column{
	padding-left:20px;
	padding-right:20px;
}
#issue-content .issue-side-column{
	position:relative;
	padding-right:20px;
	background-color:#EBF2F9;
}
#issue-content .issue-side-column:after{
	content:' ';
    position:absolute;
    top:0;
    left:0;
    bottom:0;
    width:10px;
    background: linear-gradient(to left, RGBA(30, 56, 74, 0) 0%, RGBA(30, 56, 74, 0.15) 100%);
}

#issue-content .toggle-title{ background:none; }
}
