css:
@-moz-document domain("furstre.am") {
  /* START general definitions */
  
  /* Selection color and custom scrollbar for Chrome */
  ::-moz-selection {
    background-color: #5000c7 !important;
    color: #d6baff !important;
  }
  ::selection {
    background-color: #5000c7 !important;
    color: #d6baff !important;
  }
  ::-webkit-scrollbar {
    width: 0.929em;
  }
  ::-webkit-scrollbar-thumb {
    background-color: rgb(148, 107, 209);
    border-radius: 0;
  }
  ::-webkit-scrollbar-thumb:hover {
    background-color: rgb(116, 83, 166);
  }
  ::-webkit-scrollbar-track {
    background-color: rgb(24, 0, 59);
  }
  
  /* Default color of main background on all parts of the site except forum */
  .body {
    background-color: #080014 !important;
  }
  
  /* Some places don't use a ".body" class, so we just make the regular body tag have the correct background (forum and dashboard) */
  /* Don't worry, it won't overwrite the custom background of anyone's stream */
  body {
    background-color: #080014 !important;
  }
  
  /* Define the color of links on the site */
  a {
    color: #32ff85 !important;
  }
  
  /* Define the default text color */
  p {
    color: #d7bdff;
  }
  
  /* Default color for <small> text */
  small {
    color: #1a8a47 !important;
  }
  
  /* Default heading color */
  h4, h3, h2, h1 {
    color: #ab73ff;
  }
  
  /* Default input box colors */
  input, .form-control {
    background-color: #18003b !important;
    color: #ebdeff !important;
    border-color: #5000c7 !important;
  }
  input:hover, input:focus, .form-control:hover, .form-control:focus {
    background-color: #2a0069 !important;
  }
  select {
    background-color: #18003b;
    border-color: #5000c7;
    color: #d7bdff;
  }
  
  /* Default button styles (not icons, buttons) */
  button {
    background-color: #18003b !important;
    border-color: #5000c7 !important;
    color: #d7bdff !important;
  }
  button:hover {
    background-color: #2a0069 !important;
  }
  
  /* Define the color of most of the icons (home icon, settings icon, etc.) */
  /* The buttons on top of the chat menu have a different class, and are also defined here */
  .glyphicon, .button {
    color: #946bd1 !important;
  }
  .glyphicon:hover, .button:hover {
    color: #d3b5ff !important;
  }
  
  /* Customized side menu for both main site and stream pages */
  .mcontentwrapper {
    background-color: #080014;
    border-right: 1px solid #5000c7;
  }
  .side-menu-stream .side-nav .sep, .side-menu .side-nav .sep {
    background-color: #ba94f2;
  }
  
  /* Panels: Located on the home page and above the stream player, as well as navigation menus */
  .panel {
    background-color: #18003b;
    border-color: #5000c7;
    border-bottom: medium solid #5000c7 !important;
    color: #ab73ff;
  }
  .panel-default > .panel-heading {
    background-color: #18003b;
    border-color: #5000c7;
    color: #d7bdff;
  }
  .panel strong {
    color: #ab73ff;
  }
  
  /* Labels/form-groups (I.E. Username/password text above input box) */
  .form-group {
    background-color: #18003b;
  }
  label {
    margin-left: 0.5em;
    color: #ab73ff;
  }
  
  /* Nav menu hover and active fix */
  .nav > li > a:hover, .nav > li.active > a, .nav > li > a:focus, .nav-pills > li.active > a {
    background-color: #2a0069 !important;
  }
  /* More nav menu fixes (nav-tabs class uses borders) */
  .nav-tabs > li > a:hover, .nav-tabs > li.active > a {
    background-color: #2a0069 !important;
    border-color: #5000c7 !important;
  }
  .nav-tabs {
    border-color: #5000c7;
  }
  /* Differentiate between active and hovered nav items */
  .nav > li.active > a, .nav > li.active > a:hover, .nav-tabs > li.active > a:focus {
    border-style: inset;
    border-top: thin solid #2E0073;
    border-left: thin solid #2E0073;
    border-bottom: medium solid #5000c7;
    border-right: medium solid #5000c7;
  }
  
  /* Info div (the bar at the bottom of the page) */
  .body > div {
    background-color: #080014 !important;
    border-top: 1px solid #5000c7;
  }
  .body > div > div > span {
    color: #ab73ff !important;
  }
  .body {
    overflow: hidden;
  }
  
  /* END general definitions */
  
  /* START home page */
  
  /* Fix for an adjustment made on the info div */
  .body .row {
    border: none;
  }
  
  /* Blockquotes on the panels */
  blockquote {
    background-color: #2a0b59;
    border-color: #2a0b59;
  }
  
  /* Blockquote text color (heading) */
  blockquote p {
    color: #ab73ff;
  }
  
  /* Panel text alignment fix */
  .panel p {
    margin-left: 0.7em;
  }
  
  /* Live streams grid adjustments */
  .viewers {
    color: #d7bdff !important;
  }
  .overlay, .overlay strong {
    color: #d7bdff !important;
  }
  .overlay .user h3 {
    color: #32ff85 !important;
  }
  .live-streams {
    border-color: #3c0096;
  }
  .live-streams .live-stream:hover {
    outline-color: #946bd1;
  }
  
  /* Fix header color above search bar */
  .contentwrapper h2 {
    color: #32ff85;
  }
  
  /* END home page */
  
  /* START stream directory */
  
  /* Overlay when hovering over a stream */
  .stream-dir-row .stream .overlay {
    background-color: #18003b !important;
    opacity: 0.8;
  }
  .stream .overlay strong {
    color: #32ff85;
  }
  .stream .overlay small {
    color: #d7bdff !important;
  }
  
  /* Border color for streams that are live vs offline */
  .stream-dir-row .live {
    border-color: #5000c7;
  }
  
  /* END stream directory */
  
  /* START settings pages */
  
  /* Input boxes with separate background colors fix */
  .input-group-addon {
    background-color: #18003b;
    border-color: #5000c7;
  }
  .input-group-addon small {
    color: #d7bdff !important;
  }
  
  /* Page header bottom-border color fix and font-variant */
  .page-header {
    border-color: #5000c7;
    font-variant: small-caps;
  }
  
  /* Table under API keys */
  table {
    color: #ab73ff;
  }
  
  /* END settings pages */
  
  /* START dashboard */
  
  /* Background and color adjustments */
  .dash-content {
    background-color: #080014;
  }
  .dash-content strong, .dash-content label {
    color: #ab73ff;
  }
  
  /* The input boxes are different to previous ones */
  .well {
    background-color: #18003b;
    border-color: #5000c7;
    color: #d7bdff;
  }
  
  /* END dashboard */
  
  /* START stream page */
  
  /* Change the center division that holds the player and description */
  .body .center {
    background-color: rgba(08, 00, 14, 0.8) !important;
    margin-top: -1em;
  }
  .img-thumbnail {
    background-color: #28ad5d;
    border-color: #18003b;
  }
  
  /* Top nav menu with the settings and user list buttons */
  .comunica .comunica-top {
		background-color: #080014 !important;
	}
  
  /* Chat background */
  .comunica {
    background-color: #5000c7 !important;
  }
  .comunica #comunica-chat-pane {
		background-color: #080014 !important;
	}
  
  /* Message styles */
  .comunica #comunica-chat-pane .msg .content {
		background-color: #18003b;
		color: #d7bdff;
		font-family: Helvetica;
		border-color: #5000c7;
	}
  .comunica #comunica-chat-pane .msg .content.admin {
		background-color: #000f3b;
	}
  .details .user {
		color: #ba94f2 !important;
	}
	.details .datetime {
		color: #32ff85 !important;
	}
	i.fa.fa-camera-retro {
		color: #ff2b2b;
	}
  i.fa.fa-bolt {
    color: #ffff55;
  }
  
  /* Remove the arrow on the user icon when they enter a message */
  .comunica #comunica-chat-pane .msg .content.admin:after, .comunica #comunica-chat-pane .msg .content:after, .comunica #comunica-chat-pane .msg .content.pull-right:after {
		display: none;
	}
  
  /* Change the color of the smilies icon and the background of the pop-up */
  .comunica .comunica-smiles {
		color: #ba94f2;
	}
	.comunica .comunica-smilies {
		background-color: #18003b !important;
		border-color: #5000c7 !important;
	}
  
  /* Change the color of the input area for a new message, and hover effect */
  .comunica .comunica-msg-input {
    min-height: 3.286em; /* Fix for the pop-out chat */
		background-color: #18003b;
		border-color: #5000c7;
		color: #ebdeff !important;
	}
  .comunica .comunica-msg-input:hover, .comunica .comunica-msg-input:focus {
		background-color: #2a0069;
	}
  #comunica-message-input {  /* To make sure nothing else interferes and changes the input color, making it hard to read */
    color: #ebdeff !important;
  }
  
  /* Settings and user list menus */
  .comunica .comunica-menu {
    background-color: #080014;
  }
  .comunica .comunica-menu .settings-item.clickable {
    color: #d7bdff;
  }
  .comunica .comunica-menu .settings-item.clickable:hover {
    background-color: #18003b;
  }
  .comunica .comunica-menu .settings-item h4 {
    background-color: #18003b;
  }
  .settings-item.clickable i {
    color: #32ff85;
  }
  .comunica select {
    background-color: #18003b;
		color: #d7bdff;
		border-color: #5000c7;
  }
  .comunica .comunica-menu.saveable .save-menu {
    background-color: #080014;
  }
  .comunica .comunica-menu.saveable .save-menu a.button, .comunica .comunica-menu.saveable .save-menu a.button.primary {
    background-color: #18003b;
		color: #d7bdff;
		border-color: #18003b;
		border-bottom-width: 1.429em;
  }
  .comunica .comunica-menu.saveable .save-menu a.button:hover {
		background-color: #2a0069;
		border-color: #2a0069;
	}
  .settings-item {
    color: #d7bdff;
  }
  
  /* Pop-up items */
  /* The button style */
  .comunica button {
		background: #18003b;
		border-color: #5000c7;
	}
	.comunica button:hover {
		background: #2a0069;
	}
  /* The nickname pop-up */
  .comunica .comunica-opaque-popup .comunica-pick-nick {
		background-color: #18003b;
		color: #ab73ff;
		border-style: none;
		margin-top: -0.214em;
		box-shadow: 0px 0px 4px #5000c7;
	}
  /* Lastly the part of the pop-up that hides the chat (somewhat) */
  .comunica .comunica-opaque-popup {
		background-color: rgba(8, 0, 20, 0.75);
		color: #d7bdff;
	}
  
  /* The stream player (JWplayer) styles */
  /* Loading spinner */
  .vjs-loading-spinner {
    color: #ab73ff;
  }
  /* Offline text */
  .vjs-error .vjs-error-display::before {
    color: #32ff85;
    letter-spacing: 2px;
    font-weight: bold;
    font-size: 40px;
  }
  /* Control bar */
  .vjs-default-skin .vjs-control-bar {
    background-color: rgba( 08, 00, 14, .8);
    color: #ab73ff;
  }
  /* Color of volume bar */
  .vjs-default-skin .vjs-control-bar .vjs-volume-level {
    background: #32ff85 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAYAAAAGCAYAAADgzO9IAAAAP0lEQVQIHWWMAQoAIAgDR/QJ/Ub//04+w7ZICBwcOg5FZi5iBB82AGzixEglJrd4TVK5XUJpskSTEvpdFzX9AB2pGziSQcvAAAAAAElFTkSuQmCC") repeat scroll -50% 0px !important;
  }
  /* Big play button */
  .vjs-default-skin .vjs-big-play-button{
    color: #32ff85;
    background-color: #18003b;
    border-color: #5000c7;
  }
  .vjs-default-skin:hover .vjs-big-play-button {
    background-color: #2a0069;
    border-color: #5000c7;
  }
  
  /* Force the description size to save page length and work towards getting rid of the extra scrollbar */
  .body.stream .row:nth-child(3) {
    max-height: 7.142em;
    overflow-y: auto;
  }
  
  /* Adjust margins on the rows above and below player to reduce page size */
  .body.stream .row {
    color: #ab73ff;
    margin-top: 5px !important;
    margin-bottom: 0px !important;
  }
  
  /* Move the Donate button to the right */
  .body.stream .btn.pull-right {
    margin-left: 1em;
  }
  
  /* Move the player to reduce page size */
  .player-wrap {
    margin-top: 3px;
  }
  
  /* END stream page */
  
  /* START forum */
  
  /* Search bar fix */
  .search-box-inner {
    background-color: #18003b;
    border-color: #5000c7;
  }
  
  /* Main page header */
  #header {
    background-color: #18003b;
  }
  #header li a:hover {
    background-color: #2a0069;
  }
  #subheader-menu {
    background-color: #18003b;
  }
  #faq-link, #members-link {
    border-color: #5000c7 !important;
  }
  
  /* Only the forum uses the <dl> tag */
  dl {
    background-color: #18003b;
    color: #d7bdff;
  }
  .topiclist .header dt a {
    color: #32ff85 !important;
  }
  li.header dd, .forum-name {
    color: #ab73ff;
  }
  li.row {
    border-color: #5000c7;
    border-top: 1px solid #5000c7;
  }
  
  /* "Dark header" blocks */
  .dark-header {
    background-color: #3c0096;
    color: #d7bdff;
  }
  .dark-header-content {
    background-color: #18003b;
    border: 1px solid #5000c7;
    color: #d7bdff;
  }
  .username-coloured {
    color: #ff2b2b !important;
  }
  
  /* Text at the bottom of the page */
  .rightside, .copyright {
    color: #ab73ff;
  }
  .navbar {
    border-color: #5000c7;
  }
  
  /* Login pop-up */
  .login-popup {
    background-color: #18003b;
    box-shadow: 0px 0px 20px #5000c7;
  }
  .quick-login {
    border-color: #5000c7;
  }
  #login-text {
    color: #ab73ff;
    font-variant: small-caps;
  }
  .navbar_username_outer label, .navbar_password_outer label {
    color: #d7bdff;
  }
  #autologin_label {
    color: #d7bdff;
  }
  .button2, .button1 {
    color: #ab73ff !important;
    border: 1px solid #5000c7 !important;
  }
  
  /* Parentheses around comments notification when logged in */
  .user-links li {
    color: #ab73ff;
  }
  
  /* User control panel */
  #cp-main h2 {
    color: #ab73ff;
  }
  #cp-main h3, #cp-main hr, #cp-menu hr {
    border-color: #5000c7;
  }
  dl.details dt {
    color: #ab73ff;
  }
  #navigation a {
    background-color: #18003b;
  }
  #navigation #active-subsection a, #navigation a:hover {
    background-color: #2a0069;
  }
  #tabs a span {
    color: #32ff85;
    background-color: #18003b;
  }
  #tabs li.activetab a span, #tabs a:hover span, #tabs li.activetab a:hover span {
    color: #32ff85 !important;
    background-color: #2a0069 !important;
  }
  #tabs .activetab a {
    border-color: #18003b;
  }
  .inputbox {
    background-color: #18003b;
    border-color: #5000c7;
    color: #ebdeff !important;
  }
  .inputbox:hover, .inputbox:focus {
    background-color: #2a0069;
    border-color: #5000c7;
  }
  
  /* Member list */
  a strong { /* Fix for ".pagination strong" style */
    color: #32ff85 !important;
  }
  #memberlist th {
    background-color: #18003b;
  }
  table.table1 tbody td, table.table1 tbody tr {
    border-color: #5000c7;
    color: #d7bdff;
  }
  table.table1 tbody tr:hover {
    background-color: #2a0069;
  }
  table.table1 th.name, table.table1 th.info {
    color: #ab73ff;
  }
  
  /* The more specific selectors overwrite general selectors, so the general h2 adjustment defined */
  /* above needs to be redefined to work here */
  .content h2, .panel h2 {
    color: #ab73ff;
  }
  
  /* Registration page adjustments */
  hr {
    border-color: #5000c7;
  }
  .panel h3 {
    border-color: #5000c7;
  }
  #message h2, #confirm h2 {
    border-color: #5000c7;
  }
  
  /* Login notice page */
  fieldset dl:hover dt label {
    color: #d7bdff;
  }
  dd label {
    color: #ab73ff;
  }
  a.button2 {
    background-color: #18003b;
  }
  a.button2:hover {
    background-color: #2a0069;
  }
  
  /* Forum topic pages */
  .pagination {
    color: #ab73ff;
  }
  .pagination span strong {
    background-color: #2a0069;
    border-color: #5000c7;
  }
  .pagination span a, .pagination span a:link, .pagination span a:visited {
    background-color: #18003b;
    border-color: #5000c7;
  }
  .row .pagination span a:hover, li.pagination span a:hover, .pagination span a:hover {
    background-color: #2a0069;
  }
  
  /* Forum post */
  .bg1, .bg2 {
    background-color: #18003b;
    border-color: #5000c7;
  }
  .postbody .content, .post:target .content {
    border-color: #5000c7;
    color: #d7bdff;
  }
  .signature {
    border-color: #5000c7;
  }
  .postprofile {
    border-color: #5000c7;
    color: #d7bdff;
  }
  .postprofile strong {
    color: #ab73ff;
  }
  
  /* END forum */
}
