css:
@-moz-document domain("jvforum.fr") {
.content {
    background-color:inherit;
}

.topic-title {
 	font-size:20px;   
}

a {
	color:#F92672;
}

.message .blockquote-jv {
	border-left: 3px solid #F92672;
    
}


a:hover, a:active {
	color:#F92672;
}

.forum-title a {
    color:#dddfdf;
    font-size:15px;
}

.submit:focus {
  outline: 1px solid grey;
}

.site-header {
    font-size:10px;
}

.message .blockquote-jv {
 background:rgba(126, 0, 0, 0.1);
}

.input {
	border:none;
    box-shadow:none;
}

.input:focus {
	border:none;
    box-shadow: inset 0px 0px 0px 1px #111;
}

.message.even.ignored .ignored-message {
    border-color:#2E2E2E;
    background:#2D2E2B;
    border-color:#2D2E2B;
}

.message.ignored .ignored-message {
	background:none;
	border-color:#2E2E2E;
}

.message.ignored .ignored-message {
	background:none;   
}

.message.even.ignored .ignored-message {
	background:none;   
}

.topic {
	background:#444444;   
}

.topic.even {
	background:#2E2E2E;
    
}

.message .meta-author .author {
    color:#f0f0f0;
}

.form-post .submit:hover {
	color:#F92672;
}

.message .content > .pre-jv, .message .content p > .code-jv {
	background-color:#3E3D32;   
}

body {
    color:#dddfdf;
    background: none repeat scroll 0% 0% black;
    background-color: #111;
    background-repeat: no-repeat;
    padding-bottom: 20px;
}

.message .meta-actions span:hover, .ignored-message span:hover {
 color:#F92672;   
}

.submit {
	border:none;
	background:none;
    color:#dddfdf;
    padding:none;
    text-shadow:none;
	background-color:#2d2e2b;
}

.submit:hover {
	color:#F92672;
}

.liste-topics .title,.liste-topics .nb-answers, .liste-topics .author, .liste-topics .date {
	color:#dddfdf;
}

.pages:hover {
	color:#F92672;
    
}

.form-post {
   color:#F92672;
    
}

.message .meta-actions .meta-permalink:hover {
	color:#F92672;  
}

.aside .menu .menu-content a:hover, .aside .menu .menu-content span:hover {
	background-color:#272822;
    color:#F92672;
}

.ouvrir-jvc {
    color:#f0f0f0;
    border:none;
}
.input.textarea {
    border-color:inherit;
    background-color:#2D2E2B;
    color:#f0f0f0;
    border-color:#111;
}

.input.newsujet {
    background-color:#2D2E2B;
    color:#f0f0f0;
    border-color:#f5e480;
}

.forum-title a:hover {
    color:#f0f0f0;
}

.message .content {
    color:#f0f0f0;
    border:1px solid #2E2E2E;
}


/***************************/

.js-content.content {
	background:#272822;
    border:none;
}

.js-content {
	background:#272822;
}

/***************************/

.message.even .content {
	background:#2d2e2b;
    border:1px solid #2E2E2E;
    border-color:#2E2E2E;
    
}

.site-title-link {
 	color:#e0e0e0;   
}

.message.odd {
	background-color:inherit;
}



.sheet {
    background-color:#272822;
    border-color:inherit;
    box-shadow:none;
    border:none;
}



.form-post {
    margin-top: 10px;
    border:none;
    border-radius:0;
    color:#f0f0f0;
}

.form-post-inner {
    padding:inherit;
}

.form-post .titre-bloc {
    background: none repeat scroll 0% 0% #2D2E2B;
    text-align:center;
    color:#f0f0f0;
}

.forum-title a {
    color:#e0e0e0;
}
    
.pages .active {
    background: none repeat scroll 0% 0% #2d2e2b;
    color: #e0e0e0;
    font-weight: bold;
                                            
}

.pages .active:hover {
    background-color:#F92672
}

.pages .link:hover {
    color:#F92672
}

.liste-topics .title {
    padding-top: 5px;
}

@media (min-width: 1025px) {
  .message .meta-actions span, .ignored-message span {
    cursor: pointer;
    font-size: 12px;
  }
}

.site-login-link {
    color:#f0f0f0;
}

.message .edit-mention {
    color:#F92672;
}

.aside .menu .title {
    background:#2D2E2B;
    border-radius:0;
    border-bottom:0;
    text-align:center;
    color:inherit;
}

.aside .menu .menu-content a, .aside .menu .menu-content span {
	border-bottom:none;
}

.aside .menu .menu-content {
	background:#272822;
    border-radius:0;
}

.aside .menu .menu-content a, .aside .menu .menu-content span {
    color:#e0e0e0;
}
}
