css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("login.webzen.com") {
  .user_acc_bx {
    background-image:url(http://simg.gpotato.eu/rappelz/de/download/screenshots/1_full.jpg) !important;
  }
  
  
  .bx_user_info_add {
    background-color: rgba(255,255,255,0.5) !important;
  }
  .user_acc_bx .sns_wrp {
    background-image: none !important;
  }
  
  .loading_wating_wrp { /*loading after login*/
    background-image:url(http://mochatroid.com/theme/themes/login/betterlogins1/loadingimg.jpg) !important;
    background-repeat: no-repeat !important;
    padding-left: 150px !important;
    padding-top: 150px !important;
    padding-bottom: 20px !important;
    padding-right: 0px !important;
    margin-left: 50px !important;
    margin-top: 40px !important;
    background-color: rbga(255,255,255,0.1) !important;
    overflow-x: hidden !important;
  }
  .loading_wating_wrp p, .loading_wating_wrp h1, .loading_wating_wrp strong, .loading_wating_wrp img {
    background-color: rgba(255,255,255,0.5) !important;
    padding: 20px !important;
    color: #000 !important;
  }
  .loading_wating_wrp img {
    padding-right: 200px !important;
    padding-left: 200px !important;
  }
  
  .lg_footer_wrp, .f_cl_se02 {
    background-color: rgba(255,255,255,0.1) !important;
    color: #fff !important;;
  }
  
}
