css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://www.zhihu.com/), regexp("http://www.zhihu.com/.*") {

.zh-answers-title {
  display: none;
}
.zm-votebar , .up , .down , .zm-item-vote-info , .num , .zm-item-vote , .rating , .item-actions {
  visibility: hidden;
/*   display: none; */
}
.zm-meta-panel , #zh-question-side-header-wrap  {
  display: none;
}

.zg-gray-normal , #zh-question-webshare-container {
  display: none;
}

.zm-side-section-inner h3 {
  display: none;
}
.source {
  display: none;
}
.zm-item-answer-author-wrap {
  padding-top: 20px;
  padding-bottom: 10px;
}
h2 {
  padding-top: 10px;
}
.zm-item-rich-text {
  padding-bottom: 20px;
}
.question-item-meta {
  display: none
}

.zm-profile-setion-time {
  display: none
}

}
