css:
@namespace url(http://www.w3.org/1999/xhtml);

/*
// ==UserStyle==
// @name           Google Search Fixed Topbar
// @namespace      http://sournote.org/scripts
// @description    Keep the top bar at the top instead of scrolling
// @date           2008-10-22
// @version        0.3.3
// @notes          This style is based off my Greasemonkey script which can be found
//                 on UserScripts.org at the following url:
//                 
// ==/UserStyle==
*/



@-moz-document url-prefix(http://www.google.) 
{

html body#gsr 
{
	margin: 159px 0px 0px !important;
	padding: 0px 5px !important;
}

html #gsr #header 
{
	position: fixed !important;
	top: 0px !important;
	background-color: #fff !important;
	width: 100% !important;
	left: 0px !important;
	z-index: 6 !important;
}


html #gsr #header #gbar 
{
	background-color: #fff !important;
	font-size:10pt !important;
}
html #gsr #header #gbar *
{
	background-color: #fff !important;
	font-size:10pt !important;
}


html #gsr #gbi
{
	position:fixed !important;
}

html #gsr #header #gb b, html #gsr #header #gbar b.gb1 
{
	color: #000 !important;
}


html #gsr #ssb 
{
	position: fixed !important;
	top: 97px !important;
	left:0px !important; 
	width:100% !important; 
	padding: 0px 0px !important; 
	z-index:5 !important; 
	background-color:#EBEFF9 !important; 
	-moz-border-radius: 0px !important; 
	color: #000 !important;
}

html #gsr #ssb * 
{
	font-size:8pt !important; 
	color: #000 !important;
}


html #gsr #nav 
{
	position: fixed !important; 
	top: 118px !important; 
	left: 0px !important; 
	display: block !important; 
	padding-left: 3px !important; 
	background-color: #EBEFF9 !important; 
	height: 21px !important; 
	width: 100% !important; 
	border-top: 1px solid #CDE !important;
	border-bottom: 2px solid #6B90DA !important;
	z-index: 5 !important;
}

html #gsr #nav span 
{
	display: none !important;
}

html #gsr table#nav a, html #gsr table#nav .cur 
{
	font-size: 10pt !important; 
	margin: 0px 3px !important;
}


#header+noscript+div.std 
{
	background-color:transparent !important; 
	display:block !important; 
	top:119px !important; 
	font-size:10pt !important;
	margin:0px !important;
	z-index:10 !important; 
	position:fixed !important;
	width:350px !important;
	right:2px !important;
}


div[style="padding: 4px 0pt 0pt; font-size: small;"]
{
	position:fixed !important; 
	top:138px !important; 
	left: 0px !important; 
	background-color: #EBEFF9 !important; 
	display:block !important; 
	width: 100% !important; 
	margin:0px !important; 
	border-bottom:2px solid #6B90DA !important; 
	font-size: 8pt !important;
	z-index:6 !important; 
	padding-left: 5px !important;
}


}
