css:
@-moz-document domain(whatsapp.com) {

body{
                background-image: linear-gradient(to bottom,#22211A,#4A4F4F);
          }
.app-wrapper::before {
  position: absolute;
  z-index: 1;
  background-color:rgba(98, 110, 109, 0.27);
  width: 100%;
  height: 127px;
  content: '';
  -webkit-animation: bar-slide .2s cubic-bezier(.1,.82,.25,1);
  animation: bar-slide .2s cubic-bezier(.1,.82,.25,1);
  top: 0;
  left: 0;
}
.pane-intro:after {
  content: '';
  position: absolute;
  bottom: 0;
  left: 0;
  border-top: 6px solid #626B64;
  width: 100%;
  height: 0;
}
.pane-intro {
  background-color: rgba(48, 54, 56, 0.79);
  -webkit-box-align: center;
  -webkit-align-items: center;
  -ms-flex-align: center;
 
  -webkit-box-pack: center;
  -webkit-justify-content: center;
  -ms-flex-pack: center;
 
  box-sizing: border-box;
  border-left: 1px solid rgba(0,0,0,.08);
  position: relative;
}
.intro-title {
  color: #666666;
  font-size: 36px;
  margin-top: 38px;
}
.pane-header {
  height: 59px;
  width: 100%;
  background-color: #6E6E6E;
}
.chatlist .chat.active, .chatlist .active.location {
  background-color: #3A3B3C;
  border-bottom: 1px solid #FFFFFF;
}
.chat.active .chat-status, .active.location .chat-status, .chat.active .location-secondary, .active.location .location-secondary, .chat.active .header-secondary, .active.location .header-secondary {
  color: #BAABAB;
}
.chat, .location {
  position: relative;
  cursor: pointer;
  background-color: #5E5E5E;
}
.chat-title, .location-main, .header-title {
  font-weight: 400;
  font-size: 16px;
  line-height: 21px;
  color: #000;
  -webkit-box-flex: 1;
  -webkit-flex-grow: 1;
  -ms-flex-positive: 1;
}
*::-webkit-scrollbar-track {
  background: rgba(0, 0, 0, 0.08);
}
.intro-connection-loaded .intro-image {
  background-image: url(http://i.imgur.com/onEO7zg.png);
}
.drawer-container-right, .pane-chat, .pane-intro {
  z-index: 5;
  background-color: #918D8B;
  background-size: cover;
  background-position: center center;
  -webkit-align-self: flex-end;
  -ms-flex-item-align: end;
}

 .bg-chat-loaded .pane-chat {
  background-image: url(https://lh6.googleusercontent.com/-_ASksv5DrxQ/TyQ6X4zG_7I/AAAAAAAAECg/WmVfMzj9l3Y/s1600/transparent.png);
}
.chat-status {
  font-weight: 400;
  color: rgba(0, 0, 0, 1);
  white-space: nowrap;
  -webkit-box-flex: 1;
  -webkit-flex: 1;
  -ms-flex: 1;

  text-align: left;
}
.subheader-search {
  background-color: #474C4B;
  height: 49px;
  -webkit-transition: box-shadow .18s ease-out;
  transition: box-shadow .18s ease-out;
  -webkit-transition-delay: .12s;
  transition-delay: .12s;
  padding: 0;
}
.drawer {
  background-color: #5E5E5E;
  width: 100%;
  top: 0;
  z-index: 1000;
  display: -webkit-box;
  
  -webkit-box-orient: vertical;
  -webkit-box-direction: normal;
  -webkit-flex-direction: column;
  -ms-flex-direction: column;
  
}
#startup {
  width: 100%;
  height: 100%;
  position: fixed;
  background-color: #eeeeee;
  -moz-user-select: none;
  -webkit-user-select: none;
  
  display: -webkit-box;
  
  -webkit-align-items: center;
  -webkit-justify-content: center;
}
.intro-text {
  font-size: 14px;
  color: #3F4345;
  line-height: 20px;
  margin-top: 24px;
}
.pane-body {
  -webkit-box-flex: 1;
  -webkit-flex-grow: 1;
  -ms-flex-positive: 1;
 
  overflow-x: hidden;
  overflow-y: scroll;
}
}
