/* AGENT_SHEET */
/* The Original Structure of this Code is by Zathman From Their Theme "Dark Userstyles.org 1.0"  */
/*            Some Code i Added, alot is Zathman's. i take no Credit for their Work.             */
/* To download "Dark Userstyles.org 1.0" by Zathman: http://userstyles.org/style/show/700        */
/*    Furher edited by Zealotus (user 10596). Included some tweaks from various other styles.    */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("https://userstyles.org") {
	html, body {
		background-color: #111111 !important; 
		background-image: url('http://andrewsprojects.googlepages.com/dot.png') !important; 
		background-repeat: repeat-x !important; 
		color: #DDDDDD !important;
	}
	p, h1, h2, h3, h4 {
		font-size: 1em !important;
		margin: 4pt 0 !important;
		background-color: transparent !important; 
		color: #DDDDDD !important;
	}
	a:link, a:visited, a, a.ab, .q {
		 color: #445588 !important; 
		 text-decoration: none !important;
		}
	a:hover {
		 color: #6677aa !important;
		}
	#sidebar > ul {
		margin-bottom: 1em !important
	}
	li {
		padding-bottom: 2px !important;
	}

	h1, h2, #screenshots, #style-meta, #errorExplanation,
	.screenshot-table, #donate, .linking-note, #miscellaneous,
	#show-code, #install-options {
		text-align: center !important;
		width: 100% !important
	}

	#main-header {
		background: #292929 !important;
		border: 0px !important;
		background-image: url('http://andrewsprojects.googlepages.com/userstyleslogo.png') !important;
		background-repeat: no-repeat !important;
		background-position: left !important;
	}

	#navigation {
		position: relative !important;
			top: -10px !important;
			left: 15px !important;
		}
	#navigation .current-page {
		background: #353535 !important;
		border: 0px !important;
		color: white !important;
	}
	#navigation a, #navigation a:visited {
		background: transparent !important;
		border: 0px !important;
		color: silver !important;
	}

    #left-sidebar > * {
        background-color: #333333 !important;
    }

	div#logo { display: none !important; }

	#search input[type="submit"] {
		border: 0px solid #44a !important;
		background: transparent !important;
		color: orange !important;
	}

	#main-text {
		margin-top: 3em !important;
	}

	#obsoletion-message {
		float: none !important;
		text-align: center !important;
		margin-left: auto !important;
		margin-right: auto !important;
		padding: 1em !important;
		width: 80% !important;
		background-color: transparent !important;
		border: 3px solid #3c3c3c !important;
	}

	#style_short_description { width: 100% !important; }

	#long-description, #style_long_description, textarea#css { overflow: auto !important }
	#long-description {
		text-align: justify !important; 
		margin-left: auto !important;
		margin-right: auto !important;
		width: 80% !important; 
	}

	#style-list {
		margin-top: 0.5em !important;
		-moz-column-count: 3 !important;
		font-size: small !important;
	}
	#style-list li {
		margin-right: 1em !important;
		padding-right: 5px !important
	}
	#style-list .edit-link {
		visibility: hidden !important;
		position: absolute !important
	}
	#style-list .edit-link:before {
		content: "Â±" !important;
		margin-left: 3pt !important;
		visibility: visible !important
	}
	#style-list + h2,
	#style-list + h2 + ul {
		max-width: 47% !important;
		float: right !important;
		clear: right !important;
		padding: 0 !important;
		margin: 0 !important;
	}
	#style-list + h2 + ul li {
		overflow: auto !important;
		outline: none !important;
	}
	#style-list + h2 + ul + h2,
	#style-list + h2 + ul + h2 + ul {
		max-width: 47% !important;
		float: left !important;
		padding: 0 !important;
		margin: 0 !important;
	}
	#style-list + h2 + ul + h2 + ul li {
		overflow:   auto  !important;
		outline:    none  !important;
	}
	#style-list + h2 + ul + h2 {
		margin-top: .6em !important;
		position: absolute !important;
	}
	#style-show>h2:not([id])+ul>li:last-child{ display: none !important }

	.multiple-screenshot:hover, .single-screenshot:hover {
		max-width: 100% !important;
	}

	#view-code {
		text-align: left !important;
		background-color: transparent !important;
		color: #666666 !important;
		border: 1px dashed #666666 !important;
		max-height: 20em !important;
	}

	textarea, input[id="short-description"], input[name="style[before_screenshot]"], input[name="style[after_screenshot]"] textarea[name="style[long_description]"] {
		background-color: #DDDDDD ! important;
		color: #000000 ! important;
		border: 1px dashed #666666;
	}

	input[id="long-description"] {
		height: 50px !important;
	}

	textarea#long-description {
		height: 10em !important;
	} 

	.review-container>div[id^="review-"] p{ overflow-x: auto !important }

	#comment-list { text-align: left !important; }
	#delete-comment, #update-comment, #new-comment {
		margin-top: -1em !important;
	}

	.ad { display: none !important; }
}

@-moz-document url-prefix(http://userstyles.org/style/) , url-prefix("https://userstyles.org/style/") {
	label, th {
		font-size: small !important;
		white-space: normal !important;
	}

	#before-screenshot-entry, #after-screenshot-entry,
	#control-panel {
		text-align: center !important;
		padding: 1em !important;
		width: auto !important;
	}

	input[type="submit"][value="save"] {
		position: absolute !important;
		right: 2em !important;
		top: 13em !important;
	}

	#before-screenshot-entry, #after-screenshot-entry {
		border: 1px solid rgb(221, 221, 221) !important;
	}

	.screenshot-note {
		font-size: x-small !important; 
	}
}


@-moz-document url(http://userstyles.org/login),
			   url(http://userstyles.org/user/edit_password),
			   url(http://userstyles.org/user/update_password) , url("https://userstyles.org/login"), url("https://userstyles.org/user/edit_password"), url("https://userstyles.org/user/update_password") {
	form table:not([class="form-controls"]), .two-column { margin: auto !important; }
	form, p { text-align: center !important; }
	#password, #username { width: 10em !important; }
}


@-moz-document url-prefix("http://userstyles.org/users/"),url-prefix("http://www.userstyles.org/users/"), url-prefix("https://userstyles.org/users/") {
	body>ul:not([id]) {
	margin: 0 auto !important;
	width: 15em !important;
	}

	#header-bar + h1 + ul {
		text-align: center !important;
		list-style-type: none !important;
		padding-right: 3em !important;
		margin-top: 0 !important;
	}
}


@-moz-document url-prefix("http://bugs.userstyles.org/"){
	body>table:last-child address,
	body>table:last-child .timer,
	body>table:last-child address+br,
	body>table:last-child td:last-child,
	body>div:first-child+div {
		display: none !important;
	}
}
