css:
@-moz-document url-prefix("https://www.nicovideo.jp/search/"),
               url-prefix("https://www.nicovideo.jp/tag/") {
  /* 消去する部分 */
  .banner.leadBanner.ads, .contentHeader, .contentsData, .contentsInfo, .logo, .hidariue img, .notice.banner, .contentBody.uad.searchUad.video,.itemDescription,
  .content.relatedLive, .pageTop.balloon, .itemComment, .footer, .pageTop.balloon, .toFeedback,.message, .siteHeader .hidariue{
    display:none;
  }
  /* 文字設定 */
  html,body,div,h1,.h3,h4,h5,h6,p,blockquote,pre,table,th,tr,td,ul,ol,li,dl,dt,dd,form,fieldset,legend,hr,span,a,img,strong,em,
  ins,del,cite,abbr,acronym,sup,sub,dfn,code,var,samp,kbd,b,i{
    font-weight:800;
    font-family:'ヒラギノ角ゴ Pro W3', 'Hiragino Kaku Gothic Pro', メイリオ, Meiryo, 'ＭＳ Ｐゴシック', sans-serif;
    text-decoration: none;
    color: #ccc !important; 
    border: inset 0px #222742 !important;
  }
  html, body{
    background: #1b1b1b !important;
  }
  /* ヘッダー */
  #siteHeader, #siteHeaderInner,li.videoPlayback.active a,li.comment a,li.performance a,li.imageQuality a,li.videoPlayback a, .react-grid-HeaderCell, .SearchBox-optionMenu{
      background:#2f2f2f !important;
      border: inset 0px #222742 !important;
  }

  #siteHeader #siteHeaderInner ul li:hover,li.videoPlayback.active a:hover,li.comment a:hover,li.performance a:hover,li.imageQuality a:hover,li.videoPlayback a:hover {
    background:#D2691E !important;
  }
  #siteHeader #siteHeaderInner ul li a span {
    padding: 0px 6px;
    cursor: pointer;
    color:#fff !important;
    border: inset 0px #D2691E !important;
  }
  #siteHeaderRightMenuContainer, #siteHeaderLeftMenuContainer,.nicolib-SuggestSearch-suggestions.suggestBox,.nicolib-SuggestSearch-suggestion-list.suggestBoxInner,
  .TagList .TagItem, .MainContainer-commentPanel, .react-grid-Header, .react-grid-HeaderRow, .VideoMetaOverflowMenuContainer-menu{
      background:#2f2f2f !important;
  }
  #siteHeaderRightMenuContainer a:hover, #siteHeaderLeftMenuContainer a:hover, .TagList .TagItem:hover{
    background:#D2691E !important;
  }
   #siteHeader{
      border-bottom: solid 1px #444 !important;
   }
  /* カラム */
  .columnChange li.two { display: block !important; }
  .columnChange { padding-right: 8px !important; }
  .columnChange.open, .columnChange.close { background-image: none !important; }
  .columnChange .open, .columnChange .close { display: none !important; }
  .column700-300 .sub { display: none !important; }
  .column700-300 .main {
    width: 1024px !important;
    padding-right: 4px !important;
  } 
  /* 6列表示 */
  .video .item:nth-child(4n) { margin-right: 10px !important; }
  .video .item:nth-child(4n+1) { clear: none !important; }
  .video .item:nth-child(6n) { margin-right: 0 !important; }
  .video .item:nth-child(6n+1) { clear: left !important; }
  .video .item { margin-right: 10px !important; }
  .video.videoList02 .item:nth-child(2n) { margin-right: 4px !important; }
  .video.videoList02 .item:nth-child(2n+1) { clear: none !important; }
  .video.videoList02 .item:nth-child(3n) { margin-right: 0 !important; }
  .video.videoList02 .item:nth-child(3n+1) { clear: left !important; }
  .video.videoList02 .item { margin-right: 4px !important; }

  /* タグ検索画面メイン背景 */
  .tagCaption, .header,#search_united,.tagListBox, .inner, .tags, .tagCaption,.formSearch{
    background:#1b1b1b !important;
    border: inset 0px #D2691E !important;
    box-shadow: 0px !important;
  }
  .searchOption.hidden,.wrap{ 
    background:#111 !important; 
  }
         
  .balloon.tag,.optKeyword.tab, optMylist.tab, .switchingBtn, .switchingBtn,.olumnChangeList,pagerBtn.switchingBtn, .optKeyword.tab a,
   .optMylist.tab,.optMylist.tab a,.sugoiSerachButton,.tab0,.tab1,  input{
    background:#333 !important;
    color: #eee !important;
      border-bottom: solid 1px #444 !important;
  }
  .balloon.tag:hover, .optTag.tab.active,.optKeyword.tab:hover, .optMylist.tab:hover,.pagerBtn.switchingBtn.active,.switchingBtn:hover, .switchingBtn:hover,
  .olumnChangeList:hover, .pagerBtn.switchingBtn:hover, .optKeyword.tab a:hover,.optMylist.tab a:hover,.active,.sugoiSerachButton:hover,.tab0:hover,.tab1:hover{
    color: #eee !important;
    background:#D2691E !important;
    border: inset 0px #D2691E !important;
  }

  .list, .inputText, input[type="text"],.itemContent, .itemData,.value, .count.mylist a,.count.ads a{
    color: #eee !important;
  }
  .videoSearchInput, .search_united, .searchFormB,.item{
    border: inset 0px #D2691E !important;
  }
          
  .itemTitle a{ text-shadow:#D2691E 1px 1px 2px;  }
  .formSearch, .searchFormB,.sbubmit,input{
    text-align: left !important;
    min-height: 0 !important;
    box-shadow: 0px 0px 0px 0px #D2691E !important;
    margin: 0 auto !important;
  }

  #search_united_form{
    background:#1b1b1b !important; 
    box-shadow: 0px 0px 0px 0px #D2691E inset;
  }
  #search_united{
    background:#333 !important;     
  }
}
