css:
@-moz-document url-prefix("https://www.nicovideo.jp/my") {
/* 消去する要素 */
  #videoDetailInformation, #playlist, #textMarquee, #searchResultExplorer, #playerCommentPanelOuter, #ichibaPanel,
  #leftPanel, #appliPanel, #searchResultExplorerContentWrapper, #searchResultExplorerExpand, #videoExplorerExpand, #pageFooter, #wallImageContainer,
  #alertWindow, #chipWallList, #feedbackLink, #outline.wrapper div.outer div.sidebar, #tagRelatedBanner, #nicommendContainer, #nicoIchiba, 
  #hiddenUserProfile, #videoShareLinks, #bottomVideoDetailInformation, #videoHeader .toggleDetailExpand, #foot_logo.foot_logo_image,  
  #superBanner,#videoExplorerSuperBanner, .parentVideoInfo,.shortVideoInfo, .noNicoru .nicoru-button,.nicoru-button .nicorub-icon, 
  .nicoru-button .nicorub-balloon, .socialButtons, .appliPanel, .playerTabAds,.tagRelatedNicopedia exists,.nicopediaExistsContainer,.header,
  .relatedTagHeader, .relatedTagHeader, .uadTagRelated, .itemList,.tagRelatedBanner,#videoThumbnailImage,  #slickgrid_886298message, #slickgrid_886298vpos,
  .slick-header.ui-state-default,.favVideoOwnerStar,#videoTitle,.dicIcon,h4,#footer,.tooltip,.nicoSpotAds.active, .topic_wrap, .ryokanArea,
  #playerAlignmentArea.size_normal #playerContainer.controll_panel #playerTabWrapper,.messageContainer,.lastResBody, .performanceAlert, #logo_zero, .notice, .toPageTopWrap, #footerWrap,
  .content_312, #PAGEFOOTER, #head_logo, .noListMsg, #personalFrameArea, .content h3, .tokaigi2017_my_kunsho, .chokaigi2017_residence{
    display: none !important;
  }
  /* 文字設定 */
  html,body,div,h1,.h3,h4,h5,h6,p,blockquote,pre,table,th,tr,td,ul,ol,li,dl,dt,dd,form,fieldset,legend,hr,span,a,img,strong,em,
  ins,del,cite,abbr,acronym,sup,sub,dfn,code,var,samp,kbd,b,i, #bar_search{
    font-weight:800;
    font-family:'ヒラギノ角ゴ Pro W3', 'Hiragino Kaku Gothic Pro', メイリオ, Meiryo, 'ＭＳ Ｐゴシック', sans-serif;
    text-decoration: none;
    color: #ccc !important; 
    border: inset 0px #222742 !important;

  }
  html, body, .contentWrapper, .articleBody, .content nicorepo-container, .wrapper .content, .metadata, p.group_description, .mb8p4, .userDetail .profile .stats{
    background:#1f1f1f !important;
  }
 .content nicorepo-container, .wrapper .content, .metadata{
    margin-left:250px!important;
}
  /* ヘッダー */
  #siteHeader, #siteHeaderInner,li.videoPlayback.active a,li.comment a,li.performance a,li.imageQuality a,li.videoPlayback a, .userDetail,.wrapper .sidebar,.sidebar ul li.favTab a, .wrapper .sidebar ul li,
  .nicorepoTab a, .tagrepoTab a, .mylistTab a, .videoTab a, .intro_container a, .userDetail dl.pullout dt a, .userDetail .profile ul.stats, .content .buttonOuter p .button, #history .articleBody .outer .buttonShape,
  .sidebar ul .parent .navInner, .next-page, .log-reslist, .aside, dd, #BTN_playlist_play_all , .fav_mylist_btn active, #target_t, #target_m, #search_input, .tabMenu li.tab.active a, .tabMenu li.tab a, 
  .fav_mylist_btn, .watchedCount{
      background:#2f2f2f !important;
      border: inset 0px #222742 !important;
  }
  .wrapper .sidebar ul li{
      border-top: solid 1px #1f1f1f !important;
      border-bottom: solid 1px #3f3f3f !important;
  }
.fav_mylist_btn active, #target_t, #target_m, #search_input{
      border: solid 1px #444 !important;
  }
  .wrapper .sidebar{
    position:fixed !important;
  }
  #siteHeader #siteHeaderInner ul li:hover,li.videoPlayback.active a:hover,li.comment a:hover,li.performance a:hover,li.imageQuality a:hover,li.videoPlayback a:hover, .content .buttonOuter p .button:hover,
  #history .articleBody .outer .buttonShape:hover,.userDetail .accountEdit:hover,dl.pullout dd ul li a:hover,#siteHeaderRightMenuContainer a:hover, #siteHeaderLeftMenuContainer a:hover,.wrapper .sidebar ul li:hover,
  .nicorepoTab a:hover, .tagrepoTab a:hover, .mylistTab a:hover, .videoTab a:hover, .intro_container a:hover,.sidebar ul li.favTab a:hover, .next-page:hover, .log-res-submit-button:hover,#BTN_playlist_play_all:hover , 
  .fav_mylist_btn active:hover, #target_t:hover, #target_m:hover, .tabMenu li.tab.active a:hover, .tabMenu li.tab a:hover, .fav_mylist_btn:hover{
    background:#D2691E !important;
  }
  #siteHeader #siteHeaderInner ul li a span {
    padding: 0px 6px;
    cursor: pointer;
    color:#fff !important;
    border: inset 0px #222742 !important;
  }
  #siteHeaderRightMenuContainer, #siteHeaderLeftMenuContainer,.nicolib-SuggestSearch-suggestions.suggestBox,.nicolib-SuggestSearch-suggestion-list.suggestBoxInner{
      background:#2f2f2f !important;
  }

   #siteHeader{
      border-bottom: solid 1px #444 !important;
   }
  
  #PAGEURGENT,#PAGEURGENT a{
     color:#eee !important;
     background:#ff1100 !important;   
  }
  .userDetail{
   width: 620px !important; 
    margin: 0 0 0 450px !important;
  }
  .wrapper .sidebar{
     margin: -250px 0 0 0 !important;   
  }
  .userDetail{
    border: solid 1px #444 !important;
  }
  #header {
    margin: 0 auto 0px !important;
    padding-top: 0px !important;
  }
  .miniSearchWrap{
     margin: 50px 0 0 0 !important;       
  }
  h5 a, .articleBody a{
       text-shadow:#D2691E 1px 1px 2px;
  }
  .userDetail .accountEdit, .userDetail dl.pullout dd{
      background:#444 !important;      
  }
  .log-res-input, .log-res-submit-button{
       color:#eee !important;
      border: solid 0px #444 !important;
      background:#444 !important;      
  }
}
