css:
@-moz-document url-prefix("https://live.nicovideo.jp/search") {

  /* 消去する部分 */
  .banner.leadBanner.ads, .contentHeader, .contentsData, .contentsInfo, .logo, .hidariue img, .notice.banner, .contentBody.uad.searchUad.video,.itemDescription,
  .content.relatedLive, .pageTop.balloon, .seachFormA, .itemComment, .footer, .pageTop.balloon, .toFeedback,#page_header, #siteFooter, #page_footer, #utility_link, #footer_ads,
  .copyrights, #select_mode_area img,#option_toggle_slant,.notice_msg,#search_right,#option_toggle_slant img{
    display:none;
  }

  /* 文字設定 */

  html,body,div,h1,.h3,h4,h5,h6,p,blockquote,pre,table,th,tr,td,ul,ol,li,dl,dt,dd,form,fieldset,legend,hr,span,a,img,strong,em,
  ins,del,cite,abbr,acronym,sup,sub,dfn,code,var,samp,kbd,b,i{
    font-weight:800;
    font-family:'ヒラギノ角ゴ Pro W3', 'Hiragino Kaku Gothic Pro', メイリオ, Meiryo, 'ＭＳ Ｐゴシック', sans-serif;
    text-decoration: none;
    color: #ccc !important; 
    border: 0px solid #1b1b1b !important;
  }
  .status, .thmb_area{
     color: #777 !important;    
  }
  html, body,.search_tab_list,.search_form,.search_stream_title a:hover{
    background: #1b1b1b !important;
    border: 0px solid #1b1b1b !important;
  }

    .videoInformation, .nicolib-SuggestSearch-suggestions.suggestBox, .nicolib-SuggestSearch-suggestion-list.suggestBoxInner,.inputText,.header_search,.videoSearchInput, 
  input,#select_mode_area, #option_area, #sort_type_area, #mode_bg, #option_area,#search_option,.recommend_list,#option_toggle_slant,.tag_suggest_input_block,
  .suggestion_list_for_live li{
    background:#111 !important;
    color: #ccc !important; 
    border: 0px solid #1b1b1b !important;
  }
  #search_input_block{
    background-image: linear-gradient(to bottom, #1b1b1b, #1b1b1b) !important;
    box-shadow: 0px 0px 0px 0px #1b1b1b !important;
  }
  /* ヘッダー */
  #siteHeader, #siteHeaderInner, #mode_closed_div, #mode_reserved_div,#option_toggle_box,.pager a,.tab a,#search_form_submit, #sort_select_area{
      background:#222742 !important;
        color: #ccc !important; 
      border: inset 0px #222742 !important;
  }

  #siteHeader #siteHeaderInner ul li:hover, #mode_active,#siteHeaderRightMenuContainer a:hover, #siteHeaderLeftMenuContainer a:hover,#mode_onair,
  #mode_reserved_div:hover,#mode_closed_div:hover,.pager a:hover,.pager a.active,.tab a:hover,#search_form_submit:hover, .suggestion_list_for_live li:hover{
    background:#595cce !important;
  }

  #siteHeader #siteHeaderInner ul li a span {
    padding: 0px 6px;
    cursor: pointer;
    color:#fff !important;
    border: inset 0px #222742 !important;
  }

  #siteHeaderRightMenuContainer, #siteHeaderLeftMenuContainer, #sortselect{
    background: #000 !important;
    color:#fff !important;
  }


  /* 検索画面メイン背景 */
  .tagCaption, .header,#search_united,.tagListBox, .inner, .tags, .tagCaption,.formSearch,#search_input_block,#search_left,.container,.cmn{
    background:#1b1b1b !important;
    border: inset 0px #1b1b1b !important;
    box-shadow: 0px !important;
    border: 0px solid #1b1b1b !important;
    border-radius: 0px !important;
  }

  .search_stream_title a{ text-shadow:#595cce 1px 1px 2px;  }
  .search_stream_title a:hover{ 
    color:#595cce !important;
  }
}
