@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vine.co") {
*
  {
    text-shadow:none!important;
    box-shadow:none!important;
    border-radius:0px!important;
  }
#explore-content,#outer-wrapper,.permalink-post-wrapper,.modal.login .card,#outer
  {
    background-color: #161616;
  }
header.logged-out
  {
    background-color: #161616;
    border-bottom: 3px solid #00BF8F;
  }
.card,.one-up .info,div.info,div.post-info,.post.grid .mouseover-video
  {
    background-color: #181D20;
  }
.module-container
  { 
    background: #161616 none repeat scroll 0% 0%;
  }
.featured-users .user-container
  {
    background: #1C1F22;
  }
.sidebar-video .description::after,.pinned-channel .description::after,.post.grid .description::after
  {
    background: none!important;
  }
hr
  {
    background-color: #2D343C;
  }
.comment input
  {
    background-color: #101010;
    color: #FFFFFF
  }
.global-search .search input
  {
    border-radius: 0px!important;
    background-color: #161616!important;
    border-left: 2px solid #464646!important;
  }
footer
  {
    background:#161616 none repeat scroll 0% 0%;
    border-top:1px solid #282828;
  }
li.copy
  {
    background:url("http://k30.kn3.net/39EC9C4B6.png") no-repeat scroll 0% 0% transparent;
    padding-right:250px;
  }
.follow-metrics > div.followers, .follow-metrics > div.loops
  {
    border-right: 1px solid #282828;
  }
.sidebar.user .icon,.featured-users .user-container .avatar-new img,.user-info-new.user-info-size-38 .avatar-new img,.user-info-new.user-info-size-24 .avatar-new img
  {
    border-radius:50%!important;
  }
.grid.post.grid-feed-title-card .sidebar
  {
    background:#0E1316!important;
  }
#outer-wrapper.has-timeline-header .timeline-content
  {
    background-color: #161616!important;
  }
/*--------time to fix this-----*/
  .timeline-content
  {
    background-color: #242424;
  }
header,.colorize-header header
  {
    background-color: #282828;
}
.timeline-toggles {
    background: #252C33 none repeat scroll 0% 0%;
}
.timeline-header .timeline-header-content, .mobile .sidebar-color-block, .grid .sidebar-color-block, .pagination-container .pagination, .follow, #outer-wrapper.mobile header, button.follow-button.following, button.follow-button.follow-requested, button.follow-button.settings, button.follow-button.follow:hover {
    background-color: #1D1F26;
}
.view-mode-toggles
{
    background: #121619;
}
.view-mode-toggles i
{
    border-bottom: 2px solid #242424;
}
.timeline-toggles>div:first-of-type
{
    border-right: 2px solid #242424;
}
.timeline-header.user .user-metrics a
{
    background: #252C33;
}
.timeline-header.user .icon,.current-user .avatar,#outer-wrapper.settings #settings-wrapper .avatar
{
    border-radius: 50% !important;
}

.timeline-header .title,.timeline-header.user .metric-count
{
    color: #00BF8F;
}
.timeline-header.user .loops
{
    color: #ADADAD;
}
.comments-container li
{
    color: #0380A7;
}
body
{
color:#BBB;
}
[class^=icon-]
{
    color: #00bf8f;
}
.user-info-new:not(.user-info-inherit-color) .username a
{
    color: #009E77;
}
h3.module-title, h3.module-title a
{
    color: #FCB874;
}
.one-up .title
{
    color: #6E818C;
}
#outer-wrapper.settings #settings-wrapper
{
    background: #292828;
}
#outer-wrapper.settings #settings-container .setting-row
{
    border-bottom: 1px solid #2D343C;
}
.post.permalink-post-wrapper .related-videos
{
    color: #C7C7C7;
    background: #181D20;
}
.related-videos button
{
    color: #000;
    background: #E6E35C;
}
#search-input input
{
    background: rgba(0, 0, 0, 0.3);
    color: #7af8ff;
}
#search-input input
{
    color: #00BF8F;
}
.autocomplete
{
    background: #252525;
}
#search-input ul
{
    border-right: 1px solid #404040;
}
#search-input ul a:hover
{
    color: #00bf8f;
} 
}
