css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("iichan.hk") {

/*preload*/
@font-face {
	font-family: 'Roboto Condensed';
	src: local('Roboto Condensed'), local('RobotoCondensed-Regular'), url(http://fonts.gstatic.com/s/robotocondensed/v13/Zd2E9abXLFGSr9G3YK2MsBz0dNVQi7XqpmlL2zM1TQo.woff) format('woff');
}

html{
  background: url(http://nyakiss.me/static/img/himari.png) no-repeat fixed right bottom, url(http://nyakiss.me/static/img/back.png) repeat fixed;
}
body{
  background: #eee;
  font-family: 'Roboto Condensed';
  padding: 0px;
  width: 800px;
  color: #555;
}
a{
  color: #f44556;
  text-decoration: none;
  transition: 0.3s;
}
a:hover{
  color: #ffa8be;
}
.footer, .doubledash, .banner, .rules, hr, .thumbnailmsg, .reply > br{
  display: none;
}


/*doolfix*/
div[de-thread="null"]{
  padding-left: 14px;
}
.de-cfg-head:lang(fr), #de-panel:lang(fr){
  background: #555;
}
.de-parea{
  padding: 0 14px 60px 14px;
}
#de-pform{
  padding: 5px 20px;
  background: #ddd;
  border-radius: 4px;
}
/*adminbar*/
.adminbar{
  position: absolute;
  width: 100%;
  text-align: center;
  background: #555;
  color: #aaa;
}
.adminbar a{
  color: #eee;
  font-family: 'Roboto Condensed';
  text-decoration: none;
  padding: 3px;
  display: inline-block;
}
/*postarea*/
.logo{
  padding: 25px 0 15px 0;
  color: #800;
}
.postarea{
  padding: 0 15px;
}
#postform{
  background: #ddd;
  border-radius: 4px;
  padding: 5px;
}
.postblock{
  background: #eee;
  color: #555;
  border-radius: 4px;
  padding: 0 10px;
}
input[type="text"], textarea, input[type="password"]{
  border-radius: 4px;
  background: #eee;
  border: none;
  padding: 5px;
  transition: 0.3s;
}
input[type="text"]:focus, textarea:focus, input[type="password"]:focus{
  background: #fff;
}
/*thread*/
div[id*="thread"]{
  margin: 5px 15px;
}
.reply{
  background: #ddd;
  border-radius: 4px;
  display: inline-block;
  clear: both;
  width: 764px;
  color: #555;
}
.highlight{
  border-radius: 4px;
  width: 760px;
}
.thumb{
  border-radius: 4px;
  margin: 5px 10px;
}
.reply .filesize{
  display: inline-block;
  margin-left: 0;
}
.reflink a{
  color: #f44556;
}
blockquote{
  margin: 0;
  padding: 0 5px;
}
.spoiler{
  background: #555;
  color: #555;
  transition: 0.3s;
}
.spoiler:hover{
  background: rgba(0 ,0 ,0 ,0);
  color: #555;
  transition: 0.3s;
}


/*footer*/
table[border="1"]{
  background: #ddd;
  border: none;
  border-collapse: collapse;
  margin: 0 16px;
}
table[border="1"] td{
  border: 1px solid #eee;
  width: auto;
}
table[border="1"] a{
  padding: 2px 5px;
}
input[type="submit"]{
  transition: 0.3s;
  background: #ddd;
  font-family: 'Roboto Condensed';
  font-size: 1.1rem;
  border: none;
  border-radius: 4px;
  padding: 1px 10px;
  color: #555;
}
.userdelete{
  background: #ddd;
  border-radius: 4px;
  margin-right: 18px;
  padding: 2px 6px
}
input[type="submit"]:hover{
  transition: 0.3s;
  background: #ccc;
}
input[type="submit"]:active{
  transition: 0.3s;
  background: #bbb;
}
}


@-moz-document
    url-prefix("http://iichan.hk/n/list"),
    url-prefix("http://www.iichan.hk/n/list") {
.logo, .sublogo{
  display: none;
}
body{
  font-family: 'Roboto Condensed';
  width: 100%;
  background: #555;
}
.header{
  text-align: center;
  font-size: 1.2rem;
  background: #888;
  color: #eee;
}
.list{
  background: #555;
  border: none;
}
.list div a{
  background: #555;
  color: #eee;
  padding: 3px 5px;
}
.list div a:hover{
  background: #666;
  padding: 3px 13px;
}
}
@-moz-document
  url-prefix("http://iichan.hk/n/"),
  url-prefix("http://www.iichan.hk/n/") {
.logo{
  padding-top: 60px;
}
.greeting p{
  position: absolute;
  margin-top: -30px;
  background: rgba(0,0,0,0.8);
  color: #eee;
  text-align: center;
  width: 640px;
  padding: 5px;
  margin-left: 75px;
  font-size: 1.2rem;
  
}
.news{
  background: #ddd;
  font-size: 1.2rem;
  color: #555;
}
table[border="1"]{
  border: none;
  border-collapse: collapse;
}
table[border="1"] td{
  border: 1px solid #eee;
  width: auto;
}
table[border="1"] a{
  padding: 2px 5px;
}
input[type="submit"]{
  transition: 0.3s;
  background: #ddd;
  font-family: 'Roboto Condensed';
  font-size: 1.1rem;
  border: none;
  border-radius: 4px;
  padding: 1px 10px;
  color: #555;
}
input[type="submit"]:hover{
  transition: 0.3s;
  background: #ccc;
}
input[type="submit"]:active{
  transition: 0.3s;
  background: #bbb;
}
}
