css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("korben.info") {
  
  /*Nettoyage*/
  .adavert-center, .top, .subnav, .side, .gotop, .post-share, .post-social-plugins, .post-subscribe, .post-landing-register, .univers, .information, .footer, .us_report_button, .similar-posts, .edito, .edito-footer, .topbar > .links > ul > .orange, #menu-top-nav, .carousel, .video-options, #suggested-topics, .badge-wrapper, section.post-menu-area, .textwidget, .cc-cookies {
    display: none !important;
  }
  
  /*Fix aticle type dossier*/
  .photolarge {
    background: none !important;
    height: 500px;
    width: inherit;
    margin: auto;
  }
  
  .post-title-large {
    margin-top: -90px;
  }
  
  .post-content {
    width: none;
    margin: 10px !important;
  }
  
  /*Responsive*/
  
  .post-title {
    margin-right: 5px !important;
    margin-left: 5px !important;
  }
  .topbar > .links {
    width: 94% !important;
  }
  .wrap {
    margin-left: 3% !important;
    margin-right: 3% !important;
  }
  
  .post-content, .post-text, .post-thumbnail, .wrap {
    width: auto !important;
    margin: 10px;
  }
  
  .maincontent, .main {
    width: 100% !important;
    padding: 0;
  }

  .maincontent {
    margin: 15px 0 !important;
  }

  .post-thumbnail {
    height: auto !important;
    margin: 20px 0 !important;
  }
  
  /* .post-thumbnail .image .presentation .size-full .aligncenter {
    width: 100% !important;
    height: auto;
  }*/
  
  .post-thumbnail img, .post-image img {
    width: 100%;
    height: auto !important;
    /*margin-left: calc(50% - 325px);*/
}
  
  #disqus_thread {
    display: none;
  }
  
  .col-sm-8 {
    width: 100%; 
  }
  
  .post-date {
    margin-right: 10px;
  }
  
  
  /*-------------------------------------Attention---------------------------------*/
  /*Ne marche pas avec tout les players youtube*/
   .player > iframe, .ing, .ed, .lazyloading {
    width: 100% !important;
  }

  @media screen and (max-width: 435px) {
    .topbar > .links > ul > li > .rss {
        display: none !important;
    }
  }
  
  @media screen and (max-width: 650px) {
    .wrap {
        padding: 0 10px !important;
    }
  }

  @media screen and (min-width: 650px) and (max-width: 720px) {
      .wrap {
          padding: 0 3% !important;
      }
  }

  @media screen and (min-width: 720px) and (max-width: 820px) {
      .wrap {
          padding: 0 5% !important;
      }
  }

  @media screen and (max-width: 760px) {
    .post-image img, .post-thumbnail img {
      width: 100% !important;
      margin-left: 0 !important;
    }

    .post-text img, .post-content img {
        width: 100% !important;
        height: auto;
        max-width: 100%;
    }

    .post-text iframe, .post-content iframe {
        width: 100% !important;
    }
  }
  
  
  /*Darker*/
  .main, .containervideo {
   background-color: rgba(0, 0, 0, .5);
   color: #fff;
  } 
  
  .edito {
    background: rgba(0, 0, 0, 0.3);
  }
  .edito-footer {
    background: rgba(0,0,0,0.3);
  }
  
  body, .ember-application {
    background-color: #333;
  }
  
  .post-title a{
    color: #fff;
  }
  
  .post-title a:hover{
    color: #DEDEDE;
  }

  .main .post-footer {
    /*border-bottom-color: blue;*/
  }
  
  .side {
    background: none;
    background-color: rgba(0, 0, 0, .3);
  }
  
  .side a {
    color: rgba(255,255,255,0.7)
  }
  
  .sep-sidebar{
    height: 0;
    background: none;
  }
  
  .side h2 {
    background: none;
    padding-bottom: 0;
  }
  
  input, textarea {
    background-color: rgba(179, 179, 179, 0.2);
    border: 1px solid rgb(102, 102, 102);
  }
  
  .search #searchsubmit {
    background: url("http://alicantepress.com/wp-content/themes/legatus-theme/images/icon-search.png") no-repeat scroll center center transparent;
  }
  
  .btn_comments, .side .univers-populaire ul.tags li a {
    background: rgba(0,0,0,0.5);
  }
  
  body, button, input, select, textarea{
    color: rgba(255,255,255,.6);
  }
  
  .wiki-summary {
    background: initial;
  }
  
  .wp-pagenavi a, .wp-pagenavi .current{
    /*background-image: none;
    color: rgba(255,255,255,.4);*/
    color: rgba(0,0,0,.7);
  }
  
  .wp-pagenavi .current{
    /*background: rgba(255,255,255,.1);
    color: rgba(255,255,255,.4);*/
    color: rgba(0,0,0,.7);
  }
  
  .univers {
    background: none repeat scroll 0% 0% rgba(0, 0, 0, 0.3);
  }
  
  .univers a {
    color: rgba(255, 255, 255, .5);
  }
  
  a.btn-white, .post-edit-link {
  /*  background: rgba(255,255,255,.1);
    color: rgba(255,255,255,.4);*/
    color: rgba(0,0,0,.7);
  }
  
  .post-entry p {
    color: #FFF;
  }
  
  blockquote {
   background-color: rgba(0, 0, 0, 0);
  }
  
  .main #disqus_thread {
    padding-top: 0px !important;
    margin-top: 0px !important;
  }
  
  #dsq-content{
    background: rgb(19, 24, 35) none repeat scroll 0% 0%;
  }
  
  #disqus_thread #dsq-content #dsq-comments li:nth-child(odd) {
	 background: rgb(19, 24, 35) none repeat scroll 0% 0%;
   opacity: 0.5; 
  }
  
  /*Style disqus sans Javascript*/
  #disqus_thread #dsq-content #dsq-comments li:nth-child(even) {
	background: rgb(19, 24, 35) none repeat scroll 0% 0%;
  }
  
  #disqus_thread #dsq-content #dsq-comments li div cite {
	 background: rgb(19, 24, 35) none repeat scroll 0% 0%;
   font-weight:bold;
  }
  
  label {
    color: white;
  }
  
  /*pop-up cnx*/
  #popin-2, #popin-2 .ou span {
    background-color: #333;
  }
  
  /*profil*/
  
  .profil {
    padding: 10px !important;
  }
  
  /*Style Discourse - replace disqus*/ 
  
  .comments-area {
    background: #191919;
    margin-left: 10px;
    margin-right: 10px;
  }
  
  .comments-title {
    background: rgb(19, 24, 35) none repeat scroll 0% 0%;
    color: white;
    margin-bottom: 10px;
  }
  
  .comments {
    background: rgb(19, 24, 35) none repeat scroll 0% 0%;
    /*Margin retro-compatibilité discus*/
    margin-left: 10px;
    margin-right: 10px;
  }
  
  pre {
    background: #253147 none repeat scroll 0% 0%;
    border-radius : 5px;
  }
  
  address, caption, cite, code, dfn, var {
    background: none;
    border-top: none;
  }
  
  .respond {
    background: none;
  }
  
  a {
    text-decoration: none;
    color: #32a9da;
  }

  a:hover {
    color: #6ad3ff;
    transition: color 0.2s linear 0s;
  }

  a:visited {
    color: #CC9393;
  }
  
  .comment-list .comment:nth-child(even) {
	color: gray !important;
  }
  
  /*Retro-compatibility disqus - comm paire*/
  .comments li:nth-child(even) {
	color: gray;
  }
    
  
  /*-----------------Edit com---------------------*/
  
  #topic-title h1 a {
  color: white;  
  }
  
  .d-header {
    position: fixed;
  }
  
  .wrap {
    max-width: initial;
  }
  
  .topic-body {
    width: 100%;
  }
  
  .topic-avatar, .topic-body {
    border: none;
  }
  
  article .row {
    border-bottom: 1px dotted #ffffff;
  }
  
  
}
