css:
@-moz-document url-prefix("https://trello.com") {
  body {
     font-family: 'Segoe UI', sans-serif;
 }
 .list {
     background: rgba(255, 255, 255, 0.5);
     border-radius: 0;
 }
 .list-card-labels {
     float: right;
     margin: 0;
     max-width: 200px;
     right: -6px;
     text-align: right;
     top: -2px;
 }
 .list-card-labels .card-label {
     border-radius: 3px;
     float: right;
     font-size: 9px;
     height: 16px;
     line-height: 16px;
     margin: 0 0 2px 2px;
     max-width: 60px;
     overflow: visible;
     padding: 0 3px;
     width: auto;
 }
 .list-card-details {
     padding: 4px 8px;
 }
  .card-short-id.hide {
     background: rgba(0, 255, 125, 0.8);
     border-radius: 3px 0;
     display: block;
     font-size: 11px;
     font-weight: bold;
     height: 16px;
     left: 0;
     line-height: 16px;
     padding: 0 3px;
     position: absolute;
     top: 0;
 }
 .has-cover .card-short-id.hide {
     border-radius: 0 0 3px 0;
 }
 .list-card-title {
     margin: 16px 0 10px;
 }
 .list-card-operation {
     border-radius: 3px;
     bottom: -2px;
     box-shadow: 1px 1px rgba(0, 0, 0, .3), -1px -1px rgba(255, 255, 255, 1);
     padding: 1px;
     right: -2px;
     top: auto;
 }
 .list-card {
     margin-top: 10px;
 }
 .list-card:first-child {
     margin-top: 2px;
 }
 .list-header-num-cards.hide {
     display: inline-block;
     font-size: 11px;
 }
 #board {
     overflow-y: hidden;
     overflow-x: hidden;
 }
}
