css:
@-moz-document url-prefix("http://zat.io/"), domain("www.zat.io") {
   header {
     background:none;
     height:60px;
   }
  header div.logo {
    display:none;
  }
   * {
				padding: 0;
				margin: 0;
			}
			a {
				color: #A6A6A6;
			}
			a:hover {
				color: #EEEEEE;
			}
	body {
		height: 100%;
	}
			.everything {
				min-height: 100%;
				width: 100%;
				display: block;
				position: relative;
				background: rgb(48,48,48) url("http://i.imgur.com/WBDb0Db.png") repeat fixed;
				font-family: "Segoe UI Light", "Segoe UI";
				color: #A6A6A6;
				padding-bottom: 9%;
			}
			nav {
				width: 100%;
				height: 60px;
				position: relative;
				background: none repeat scroll 0% 0% rgba(0, 0, 0, 0.15);
        margin-bottom: 10px;
			}
			nav ul {
				list-style-type: none;
				display: flex;
				text-align: center;
			}
			nav ul li {
				text-align: center;
				display: block;
				float: left;
				width: 14.25%;
				height: 60px;
				background: none repeat scroll 0% 0% rgba(0, 0, 0, 0.15);
			}
			nav > ul > :first-child {
				padding-left: 1px;
				padding-right: 1px;
			}
			nav > ul > :last-child {
				padding-left: 1px;
				padding-right: 1px;
			}
			nav ul li:hover {
				background: rgba(0,0,0,.4);
			}
			nav ul li a {
				display: block;
				line-height: 60px;
				height: 100%;
				width: 100%;
				text-transform: uppercase;
				text-decoration: none;
				color: #A6A6A6;
				font-weight: bold;
			}
			nav ul li ul, nav ul li ul li, nav ul li ul li a {
				display: none;
			}
			/* ADDED */
			nav ul li ul li {
				background-color: #262626;
			}
			nav ul li ul li:hover {
				background-color: #222222;
			}
			/* END ADDED */
			nav ul li:hover ul, nav ul li:hover ul li, nav ul li:hover ul li a {
				display: block;
				line-height: 60px;
				height: 100%;
				width: 100%;
				text-transform: uppercase;
				text-decoration: none;
				font-weight: bold;
      }
			.wrapper {
				width: 45px;
				background: none repeat scroll 0% 0% rgba(0, 0, 0, 0.15);
			}
			.apps {
				position: fixed;
				top: 0px;
				bottom: 0px;
				left: 0px;
				margin: auto;
				height: 323px;
				width: 45px;
				text-align: center;
        background: none;
				z-index: 1;
			}
			.apps ul li {
				list-style: outside none none;
				text-align: center;
				height: 45px;
				border-bottom: 1px solid #262626;
				border-right: 1px solid #262626;
				background: none repeat scroll 0% 0% rgba(0, 0, 0, 0.15);
			}
			.apps ul > :first-child {
				border-top: 1px solid #262626;
			}
			.apps ul li:hover {
				background: rgba(0,0,0,.4);
			}
			.apps ul li a, .apps ul li a i {
				display: block;
				line-height: 45px;
				text-decoration: none;
				color: #A6A6A6;
			}
			.footer {
				width: 100%;
				height: 60px;
				position: relative;
				background: none repeat scroll 0% 0% rgba(0, 0, 0, 0.15);
				text-align: center;
				line-height: 30px;
				font-weight: bold;
				color: #A6A6A6;
			}
			.footer a {
				color: #A6A6A6;
				text-decoration: none;
			}
			.footer a:hover {
				color: #EEEEEE;
			}
			.chat {
				text-align: center;
				display: block;
				position: relative;
				width: 100%;
				margin: auto auto;
			}
			.lpt {
				width: 40%;
				background: none repeat scroll 0% 0% rgba(0, 0, 0, 0.15);
				padding: 1.5%;
				text-align: center;
				margin-bottom: 10px;
        line-height: initial;
			}
			.lpt span {
				font-weight: bold;
			}
      .lpt strong {
        font-weight: bold;
      }
      .lpt b {
        font-weight: bold;
      }
}
