css:
@namespace url(http://www.w3.org/1999/xhtml);
/*
@-moz-document url("http://stackexchange.com/") */
@-moz-document domain("stackexchange.com")

{

  body {
    color: #f00 !important;
    background: linear-gradient(#232323, #000, #232323) !important;
}
  body a {
    color: #a9a9a9 !important;
  }
  .owner a {
    color: #d5d5d5 !important;
    font-weight: bold !important;
  }
  
  #content {
    background-color: #2d2d2d !important;
    border-top: 1px solid #FFF;
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.2);
    padding-bottom: 30px;
}
  
  .topbar {
    background: linear-gradient(#232323,#000,#232323) !important;
    border-bottom: 1px solid #d7d7d7 !important;
}
  /*search bar*/
  .topbar .topbar-links .search-container input[type="text"]:focus {
    color: #aaa !important;
}
.topbar .topbar-links .search-container input[type="text"] {
    color: #888 !important;
    border: 1px solid #232323 !important;
    padding-top: 2px !important;
    padding-bottom: 2px !important;
    background-color: #050505 !important;
    background-position: -269px -92px !important;
    padding-left: 22px !important;
    border-radius: 10px !important;
}
  /*user info*/
  .module {
    color: #070 !important;
}
  .users-sidebar .userDetails .sb-siteName a {
    font-weight: bold !important;
  }
  .module a {
    color: #ccc !important;
    font-weight: bold !important;
  }
  /*tags*/
  .post-tag {
    color: #0a0 !important;
    font-size: 11px;
    background: radial-gradient(#343434,#151515) !important;
    background-color: #ddd !important;
    border-width: 1px;
    border-style: solid;
    border-color: #121212 !important;
}
  /*general*/
  .container {
    background: #121212 !important;/*header*/
    border-bottom: 1px solid #E2E2E2 !important;
}
  /*info box header*/
  #herobox-mini #hero-content {
    background-color: #757575;
    border: 2px dotted #800 !important;
}
  
  .post-text {
    color: #999 !important;
}
  .comments {
    color: #999 !important;
}
  .comments a {
    color: #c8c8c8 !important;
    text-shadow: 0px 0px 3px #0f0 !important;
  }
  a.comment-user {
    font-weight: bold !important;
    color: #090 !important;
    text-shadow: 0px 0px 3px #95f !important;
  }
  .user-details {
    color: #990 !important;
  }
  .user-details a {
    color: #c7c7c7 !important;
    font-weight: bold !important;
}
  .reputation-score {
    color: #cc0 !important;
}
  .user-details .badgecount {
    color: #990 !important;
}
  /*footer*/
  #footer {
    clear: both;
    color: #444 ;
    padding: 0px 0px 15px;
    background: linear-gradient(#000,#232323,#000) !important;
  }
  /*2 min tour info box*/
  #herobox #hero-content {
    background-color: #444;
    border: 4px double #C8BFAC;
    background-image: none;
  }
  /*ui fix*/
  #mainbar, #sidebar, #mainbar-full, .mainbar-full {
    background: rgba(120,120,120,0.25) !important;
  }
  #footer .top-footer-links, #footer #footer-sites {
    padding: 15px;
    background: rgba(120,120,120,0.25) !important;
  }
  #footer .top-footer-links, #footer #footer-sites {
    padding: 15px;
    background: rgba(120,120,120,0.25) !important;
  }
  #footer th {
    color: #777 !important;
    text-decoration: underline !important;
  }
  #question-header {
    background: rgba(120,120,120,0.25) !important;
  }
  .new-post-activity, #new-answer-activity {
    text-align: center;
    background-color: rgba(120,120,120,0.35) !important;
    font-size: 13px;
  }
  /*removed info box*/
  #herobox-mini{
    display: none !important;
  }
  /**/
  .new-login-form .form-item {
    color: #b5b5b5 !important;
    background: rgba(120,120,120,0.35) !important;
    border: 1px solid #D4D4D4;
    box-shadow: -1px 1px #F2F2F2;
  }
  
.preferred-login {
    background: rgba(120,120,120,0.35) !important;
    width: 258px;
    border: 1px solid #D4D4D4;
    box-shadow: -1px 1px #F2F2F2;
    color: #b5b5b5 !important;
  }
.mdhelp-tabs, .wmd-help-button.active-help {
    background-color: rgba(120,120,120,0.25) !important;
  }
  
  /*removed underline*/
  
#sidebar #newsletter-ad, #sidebar .ad-container, #sidebar .everyonelovesstackoverflow {
    padding: 15px;
    border-bottom: 3px solid #F4F4F4;
    margin: 0px;
}
  /*hp*/
  /*featured site module/widget*/
  .heading, .module h3, .subheader h1 {
    color: #999;
    font-size: 15px;
    margin: 3px 0px;
    font-weight: bold;
    line-height: 27px;
  }
  .featured-site .site-details {
    color: #777 !important;
    font-size: 12px;
  }
  .featured-site .stats-label {
    font-size: 12px;
    font-weight: 700;
    color: #777 !important;
  }
  /*footer fix*/
  footer {
    padding: 30px 10px !important;
    font-size: 12px;
    text-shadow: 0px 0px 3px #0f0 !important;
    color: #999 !important;
  }
.page-numbers {
    color: #999 !important;
    background: linear-gradient(to bottom, #FFF, #E7E7E7) repeat scroll 0% 0% transparent;
    border: 1px solid #D2D2D2;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.1);
    text-shadow: 0px 0px 3px #000;
    border-radius: 15px;
  }
    .question-status {
    background-color: rgba(120,120,120,0.25) !important;
    color: #000;
    border-width: 1px;
    border-style: solid;
    border-color: #CCC #E3E9E8 #FFF;
}
  .bottom-notice {
    color: #959595 !important;
    background-color: rgba(120,120,120,0.25) !important;
}
  /*page number fix*/
  .page-numbers {
    background: radial-gradient(#343434, #151515) !important;
    border: 1px solid #D2D2D2;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.1);
    text-shadow: 0px 1px 0px #FFF;
  }
  .page-numbers {
    text-shadow: 0px 0px 3px #000 !important;
  }
  /*removed ads*/
  .ad-container {
    display: none !important;
  }
  #newsletter-ad {
    display: none !important;
  }
  /*chat rooms*/
  .ad502-room {
    position: relative;
    padding: 5px;
    overflow: hidden;
    background: rgba(120,120,120,0.25) !important;
    border-width: 1px;
    border-style: solid;
    border-color: #DDD #CCC #CCC #DDD;
    box-shadow: 0px 1px 3px #343434 !important;
    border-radius: 5px;
    margin-top: 5px;
  }
  #herobox {
    display: none !important;
  }
  /*rev 2*/
  textarea {
    font-family: "Open Sans","Helvetica Neue",Helvetica,Arial,sans-serif;
    background: #121212 !important;
    color: #d3d3d3 !important;
    border: 1px solid #CCC;
    font-size: 14px;
    padding: 8px 10px;
  }
  .mdhelp {
    background-color: rgba(180,180,180,0.6) !important;
    position: absolute;
    right: 0px;
    width: 630px;
    color: #000;
    top: 42px !important;
  }
  .mdhelp a {
    color: #cccccc !important;
    text-shadow: 0px 0px 9px #000 !important;
  }
  .grippie {
    border-style: solid;
    border-color: #DDD;
    background-color: rgba(160,160,160,.065) !important;
  }
  #sidebar .module {
    padding: 15px;
    border-bottom: 3px solid rgba(0,0,0,0.25) !important;
    margin-bottom: 0px;
  }
  /*stack overflow*/
  #sidebar .module.community-bulletin {
    padding: 10px;
    background-color: rgba(120,120,120,0.25) !important;
    border: none !important;
    color: #444;
  }
  div#hireme.orange, div.clc-jobs.orange {
    background-color: #000075;
    border-color: #000076;
    background-position: center 3px;
  }
  div#hireme > ul, div.clc-jobs > ul {
    margin: 0px;
    padding: 12px 0px 0px;
    list-style: outside none none;
    display: block;
    background-color: #343434;
  }
  div#hireme, div.clc-jobs {
    color: #333;
    text-shadow: 0px 0px 9px #000 !important;
  }
  #hmenus .nav ul li a {
    padding: 4px 10px 5px;
    color: #d4d4d4 !important;
    font-weight: bold !important;
    text-shadow: 0px 0px 9px #000 !important;
    background: #757D73 none repeat scroll 0% 0% !important;
  }
  /*rev 3*/
    #custom-header .nav-global {
    background: linear-gradient(#000,#343434) !important;
    width: 100%;
    height: 30px;
    line-height: 19.2px;
    display: block;
    z-index: 101;
    position: relative;
    box-shadow: 0px 2px 2px rgba(0, 0, 0, 0.2);
    font-family: Ubuntu,Arial,"libra sans",sans-serif;
    font-weight: 300;
  }
  /*a*/
  #custom-header .nav-global .more.open {
    background: rgba(120,120,120,0.25) !important;
    min-width: 120px;
    border-left: 1px solid #D7D7D7;
    border-right: 1px solid #D7D7D7;
  }
  /*b*/
  #custom-header .nav-global ul li ul {
    display: none;
    float: none;
    background: rgba(120,120,120,0.5) !important;
    min-width: 120px;
    top: 30px;
    border-top: 1px solid #D7d7d7;
    box-shadow: 0px 2px 2px rgba(0, 0, 0, 0.3);
  }
  #custom-header .nav-global ul li ul li a {
    font-weight: bold !important;
    text-shadow: 0px 0px 9px #000 !important;
    color: #d7d7d7 !important;
  }
  /*ask footer fix*/
  #footer .footerwrap {
    background-color: rgba(120,120,120,0.25) !important;
  }
  .show-votes .sidebar-linked .answer-votes, .show-votes .sidebar-related .answer-votes {
    background-color: #000 !important;
    color: #d8d8d8 !important;
    font-size: 90%;
    font-weight: normal;
    margin-right: 5px;
    padding: 2px 0px;
    margin-top: 2px;
  }
  /*code*/
  pre {
    background-color: #000 !important;
    border-right: 1px solid #555 !important; /*beta*/
    border-bottom: 1px solid #555 !important;
  }
  code {
    background-color: #000 !important;
  }
  /*quotes*/
  blockquote {
    margin-bottom: 10px;
    padding: 10px;
    background-color: #555 !important; /*gamma*/
    border-left: 2px solid #343434 !important;
    text-shadow: 0px 0px 9px #000 !important;
  }
    .owner { /***/
    background-color: #555 !important;
  }
  /**/
  .everyonelovesstackoverflow {
    display: none !important;
  }
  .module h4 {
    font-size: 14px;
    color: #d3d3d3 !important;
    font-weight: bold !important;
  }
  .label-key b, .label-key strong {
    color: #c4c4c4 !important;
    font-weight: normal;
  }
}
