css:
@-moz-document domain(tistory.com) {
* {
      font-family: "KoPubBatang", "나눔명조", "맑은 고딕", "굴림", "바탕" !important;
      text-indent: 0 !important;
  }

  #wrap {
      width: 800px;
      margin: 0 auto;
      float: none;
  }

  #sidebar {
      width: 100%;
      margin: 0 auto;
      float: none;
      position: relative !important;
  }

  body, #header,
  #wrapTop, .tLeft, .tCenter , .tRight,
  #wrapMid, .mtLeft, .mCenter, .mRight,
  #wrapBtm, .btLeft, .bCenter, .bRight {
      background: none !important;
      background-color: white !important;
  }

  #content,
  .entry,
  .entry .article, span,
  .txc-textbox {
      margin: 0 !important;
      display: block !important;
      max-width: 700px !important;
      border: none !important;
      background: none !important;
      text-align: left !important;
      color: black;
      background-color: white;
      line-height: 1.85em !important;
  }

  span > strong > span,
  strong > span > u > span {
      font-size: larger !important;
      font-weight: bold !important;
      color: black !important;
  }

  .entry .article, span {
      font-size: medium !important;
  }

  .entry .article p{
      _text-align: justify !important;
  }

  .boxMid, .boxMidL, .boxMidR,
  .titleWrap, .title, .titleWrap, .titleWrap h2
  .container, #container, #wrapContent, #content { 
      border:none !important;
      background-color: white !important;
      color: black !important;
  }

   .titleWrap h2 a, .titleWrap h2 a:visited { 
      font-size: x-large !important;
      background-color: white !important;
      color: blue !important;
  }

  .another_category {
      display: none !important;
  }
}
