css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("live.nicovideo.jp") {
/* ニコ生 */
  #wall_canvas,.feedback_link, #page_footer, #body_footer_wrap,.utility_link,.page_top,#watch_zapping_box,#live_header .logo a,.pagerArea,.commu_caption, .commu_info,
  #tab_title_area,.btn_tree_text,#picup_contents_area,.feature_area,.blog_area,#page_info,.search_program_area,#siteFooter,.copyrights,.ticker,#closed_recent_program_area,
  #comingsoon_recent_program_area,.for_premium,.program_area_header,.channel-streams-title,#channel-block,#contents-block,#recent_balloon_area,#eden_pickup_program_area,
  #ad_bnr,#web_pc_sidewall_left,#web_pc_sidewall_right,#middle_ad,#footer_ads, .live_topic_area,#header-ad, #billboard-ad,
  aside,#informationArea, #foot-area,div[class*="information-bar"],div[class*="foot-area"]
  {
    display: none !important;
  }
  /* 文字設定 */
  html,body,div,h1,.h3,h4,h5,h6,p,blockquote,pre,table,th,tr,td,ul,ol,li,dl,dt,dd,form,fieldset,legend,hr,span,a,img,strong,em,
  ins,del,cite,abbr,acronym,sup,sub,dfn,code,var,samp,kbd,b,i{
    font-weight:800;
    font-family:'ヒラギノ角ゴ Pro W3', 'Hiragino Kaku Gothic Pro', メイリオ, Meiryo, 'ＭＳ Ｐゴシック', sans-serif;
    text-decoration: none;
    color: #ccc !important; 
    border: inset 0px #222742 !important;
  }

  html, body,#future_program_area,#comingsoon_program_area,.official_recent carousel,#passed_program_area,#closed_program_area,.k-days,.k-header,.item.ts_play,#timeline,
  .item_image,.item_text.js_like_dialog_content,#page_cover,.textbox,.tag_suggest_input_block,#header_search_form_input,
  div[class*="contents-area"]{
    background: #1b1b1b!important;
    border: none!important;
    color: #eee !important;
  }

  /* ヘッダー */
  #siteHeader, #siteHeaderInner,li.videoPlayback.active a,li.comment a,li.performance a,li.imageQuality a,li.videoPlayback a, #live_global_nav .nav_list li.editstream a, .container .streams_hdg,div[class*="program-detail"],input[class*="text-box"]{
      background:#2f2f2f !important;
      border: inset 0px #222742 !important;
  }
  
  h2[class*="title"]{
    color: #ddd !important;
    text-shadow: #D2691E 1px 1px 2px;
    text-decoration: none !important;
  }
  #siteHeader #siteHeaderInner ul li:hover,li.videoPlayback.active a:hover,li.comment a:hover,li.performance a:hover,li.imageQuality a:hover,li.videoPlayback a:hover, #live_global_nav .nav_list li.editstream a:hover, .live_list .item:hover {
    background:#D2691E !important;
  }

  #siteHeader #siteHeaderInner ul li a span {
    padding: 0px 6px;
    cursor: pointer;
    color:#fff !important;
    border: inset 0px #222742 !important;
  }

  #siteHeaderRightMenuContainer, #siteHeaderLeftMenuContainer,.nicolib-SuggestSearch-suggestions.suggestBox,.nicolib-SuggestSearch-suggestion-list.suggestBoxInner, .programList, .infoBoxArrow{
      background:#2f2f2f !important;
  }

  #siteHeaderRightMenuContainer a:hover, #siteHeaderLeftMenuContainer a:hover, span[class*="program-type"]{
    background:#D2691E !important;
  }
  #siteHeader{
    border-bottom: solid 1px #444 !important;
  }

  #live_global_nav .nav_list li a.active {
    color: #000;
    background: #D2691E;
  }

  #page_header {
   background: none repeat scroll 0 0 #111 !important;
   border-color:#272727!important;
  }

  #commu_info,.title_text,#description_tab_view,#live_header .score_search,.cover,input[class*="text-box"] {
    color: #eee !important;
  }

  #live_header .score_search .search_word {
    background-color: #444!important;
    border: 0px solid #555!important;
  }
}
