css:
@-moz-document url-prefix("https://2ch.hk/") {
body{
     margin: 0px;
    padding: 0px;
    margin-left: 10%;
    margin-right: 10%;
    margin-top: 30px;
    box-shadow: 0 0 10px rgba(0,0,0,0.5); 
    background-color: #f9f9f9;
   
}
html{
    background-color: #999;
}
a{
	color: #777;
    text-decoration: none;
}
.menu{
    background-color: #000;
    margin: -2px;
    padding: 10px;
	font-size: 18px;
}
.menu a{
    color: #eee;
}
.menu select{
	color: #eee;
	background-color: #444;
    border: 0px;
}
.menu input[type="text"]{
	color: #eee;
	background-color: #444;
	border: 0px;
	font-size: 14px;
}
.menu input[type="submit"]{
	color: #eee;
	background-color: #222;
	border: 0px;
}
#title{
color: #333;
	text-shadow: none;
}
.thread{
	padding: 5px;
}
.rmenu{
	padding: 5px;
}
.reply{
box-shadow: 0 0 3px rgba(0,0,0,0.3); 
	border: 0px;
	background-color: #f5f5f5;
}
hr{
	display: none;
}
.top-user-boards{
	display: none;
}
}
