css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {

/*Setting minimum body width and background*/

      body {
            min-width:820px;
            background-color:white;
           }

/*Change position of text on main-page*/

      #main-text {
                  margin-top:80px;
                  text-align:justify;
                  font-size:15px;
                 }

/*Changing sidebar*/

	#miscellaneous { display:none!important }
	#sidebar H2:first-child + UL + H2, #sidebar H2:first-child + UL + H2 + UL + H2 + UL + H2  {margin-top:30px!important}
	#sidebar {-moz-column-count: 2; height:400px!important;margin-top:40px!important;padding-bottom:20px;max-width:700px!important}
	#sidebar UL {line-height:28px;font-size:12px;padding-left:10px!important;width:350px!important}

/* Remove Header-bar's Background*/

      #header-bar {
                   background:none!important
                  }

/* Changing All Headers*/

      h1, h2 {
              text-align:center; 
              color:#426395; 
              font-style: oblique; 
              font-family:serif none!important; 
              line-height:230%;
              font-variant: small-caps;
             }

/* Changing Headers on /styles page */

      h2 {
          text-align:left!important;
          font-variant: normal;
         }

/*Changing top-header's appearance*/

      #header-bar a,
      #header-bar input,
      #header-bar span    
                  {
                    border:none           !important;
                    background:none       !important;
                    font-family:serif none!important;
                    font-style:oblique;
                    color:#426395         !important;
                    text-transform: uppercase;
                  }

/*Three-column display of styles*/

      #style-list{
                  line-height:28px;
                  -moz-column-count: 3 !important;
                  font-size: small;
                 }

/* Change bullets to numbers in the lists of styles */

      #style-list li {
                      list-style-type: decimal !important;
                     }

/* Shifting the position of buttons on style's page and text after it */

      UL > LI[id^="stylish"],
      LI[id^="stylish"] + LI  {
                                position:relative;
                                  left:-40px
                               }

/* Remove ugly round bullets */

      UL > LI[id^="stylish"],
      LI[id^="stylish"] + LI,
      #sidebar ul>li,
      .category-style-top ul>li,
      #feedback + UL,
      #header-bar + H1 + UL     {
                                  list-style-type: none !important;
                                }

/* Use prettier bullets where appropriate :) */

      #sidebar ul>li,
      .category-style-top ul>li,
      #feedback + UL
                            {
                             list-style-image: url("data:image/jpeg;base64,")
                            }

/*Adding bullets to "Browse all ... styles" and "Post your style" links */

      .all-link:before,
      .post-your-style:before
                             {
                              content:url("data:image/jpeg;base64,")" "
                             }

/*Changing "Post your style" position*/

      .post-your-style {
                        text-align:left!important; 
                       }

/*Changing "Browse all ... styles." position*/

      .all-link        {
                        position:relative; top:12px
                       }

/* Transforming links */

      A {
         text-decoration:none!important;
         font-style: oblique;
        }

      A:visited {
                 color:#224375!important;
                }

      A:visited:hover {
                       color:#5273A5!important;
                      }

/* Removing dotted link border*/

      A {
         outline: none;
        }

/* Transforming main logo */

      #logo a,
      #logo a:visited {
                       font-variant: small-caps;
                       text-transform: capitalize;
                       font-size:38px
                      }

/* Obsolete styles */

      A.obsolete:before,
      A.obsolete + A.edit-link:before {
                                       content:"Obsolete style";
                                       visibility:visible!important;
                                       color:gray;
                                      }
      A.obsolete,
      A.obsolete + A.edit-link {
                                visibility:collapse!important;
                               }

/*Adding start to highly ranked styles*/

/* 5 */    A[title="Rating: 5.00"]:before,
           A[title^="Rating: 4.9"]:before,
           A[title^="Rating: 4.8"]:before,
           A[title^="Rating: 4.7"]:before,
           A[title^="Rating: 4.6"]:before,
           A[title^="Rating: 4.5"]:not([title="Rating: 4.50"]):before
                  {
                   content:url("data:image/gif;base64,")"  "
                  }

/* 4+ */   A[title="Rating: 4.50"]:before,
           A[title^="Rating: 4.4"]:before,
           A[title^="Rating: 4.3"]:before,
           A[title^="Rating: 4.2"]:before,
           A[title^="Rating: 4.1"]:before,
           A[title^="Rating: 4.0"]:not([title="Rating: 4.00"]):before
                  {
                   content:url("data:image/gif;base64,")"  "
                  }

/* 4 */    A[title="Rating: 4.00"]:before,
           A[title^="Rating: 3.9"]:before,
           A[title^="Rating: 3.8"]:before,
           A[title^="Rating: 3.7"]:before,
           A[title^="Rating: 3.6"]:before,
           A[title^="Rating: 3.5"]:not([title="Rating: 3.50"]):before
                  {
                   content:url("data:image/gif;base64,")"  "
                  }

/*0+,1+*/  A[title^="Rating: 0."],
           A[title^="Rating: 1."]
                  {
                   opacity:1 !important;
                   color:gray!important;
                  }

/* Center style's meta-info: stars, author's name, last updated, number of installs*/

           #style-meta {
                        text-align:center
                       }

	.screenshot-table  {display:block!important;text-align:center!important;}
	.screenshot-table *{display:inline-block!important}
	.screenshot-table tbody,.screenshot-table tr,.screenshot-before,.screenshot-after,.screenshot  {max-width:100%!important}
	.screenshot-box, .screenshot-box > img {text-align:center;max-width:100%!important}
	#after-screenshot-entry{max-width:0!important}

	#comment_comment {margin-bottom:7px!important}
	label.rating {border:1px solid #7F7D73 !important;padding:5px 20px!important;}
	h3#i-say + blockquote {text-align:center!important;}
	#submit-comment-button {float:none!important;padding:4px 20px;position:relative;top:-1px}
	#delete-comment-button {padding:4px 20px;position:relative;top:-40px;left:4px}

	#show-button,
	button[onclick="stylishInstall(event)"],
	button[onclick="stylishUpdate()"],
	#userjs-install,
	input[value="Save"],
	input[value="Log in"] {padding:4px 20px;}

	#regular-login input[value="Log in"]{position:relative!important; top:20px;}
	#regular-login #password {width:150px!important;}
	#regular-login fieldset > p {position:relative!important;top:25px;font-size:14px!important;text-align:center!important}
	#regular-login fieldset label {font-size:14px!important;font-weight:normal!important;}
	#regular-login tr+tr+tr td{text-align:center!important;}
	#Panel {background:none!important;}
	#Session{position:relative!important;left:-1%;}



	[id*="CommentBody"] img {max-width:100%!important;}
	[id*="CommentBody"]{overflow-x:auto!important;}
	#Comments code{white-space:normal!important;overflow-x:auto!important;}

}

/*Adding bullets on user's page*/

@-moz-document 
url-prefix(http://userstyles.org/users/) 
, url-prefix("https://userstyles.org/users/") {

#header-bar + H1 + UL:not([id]) 
	{
	 list-style-image: url("data:image/jpeg;base64,");
	 text-align: center !important;
	 padding-right: 3em !important;
	 margin-top: 0 !important;
	}

 #header-bar + h1 + ul:not([id]) li {
	display: inline-block !important;
/*	border: 1px solid #82a5da !important;      */
	-moz-border-radius:4px;
	margin:5px 0!important;
	padding:3px 15px!important;
	font-size:12px!important;
	text-align:left!important;
	vertical-align:middle!important;
  }

#header-bar + H1 + UL:not([id]) li:before {content: url("data:image/jpeg;base64,")"  ";}

}
