css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fictionpress.com") {

div.myborder_only_top div[style="float: right; margin-left: 10px; margin-bottom: 10px;"],
/* front */
center div[align="center"], 
/* top */

table[width="728"][height="90"][cellpadding="1"], 
br + center *,
/* bottom */

table.myborder tr td[valign="top"] div:not([id="bio"])

{display:none !important;} 

/*end*/

span.storytext, span#storytext {
  display: block !important;
}


body {
  background: #333333 url(data:image/png;base64,) fixed repeat-x top left !important;
  color: #CCCCFF !important;
  font-family:verdana !important;
}

a {color: #B4CDCD !important; }
a:visited {color:#668B8B !important; }
a:hover,a:visited:hover {color: #E0EEEE !important; }

a.menu-link { color: #585858 !important; }
a.menu-link:visited { color: #8d8d8d !important; }
a.menu-link:hover,a:visited:hover{ color: #fff !important; }

select, input, textarea {
  background: #222 !important;
  color: #CCCCFF !important;
  border: 1px solid #000 !important;
  padding: 1px !important;
  -moz-appearance: none !important;
}

select {margin:0 2px 1px 2px !important;}

select:hover,select:focus,input:hover,input:focus,textarea:hover,textarea:focus { border: 1px solid #999 !important; }

td { background: none !important; }

table.myborder { background: #000 !important;}
.menulinks { color: #CCCCFF !important;}
.menulinks a, .menulinks a:visited { color: #999 !important; }
.menulinks a:hover, .menulinks a:visited:hover { color: #E0EEEE !important; }

.xxmenu, form[name=fido] table tr td { background: #1e2125 !important; }
form[name=fido] table tr td img { display: none !important; }

.menu-child { background: transparent !important; border: none !important; border-bottom:1px solid #999 !important }
.menu-child a { color: #8d8d8d !important; }
.menu-child a:visited { color: #888  !important; }
.menu-child a:hover { color: #a9a9a9 !important; }


hr { border-color: #fff !important; border: none !important; border-bottom: 1px solid !important; height: 0px !important; }

.x-high { background-color: #444444 !important; }

div.menu-child form {
  width: 50% !important;
}


}

@-moz-document url-prefix(http://www.fictionpress.com/atom) {
/* Extra Recolor: Make the atom feeds readable in Firefox. */
#feedError, #feedBody { background: #111 !important; }

}
