css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("muk-bonn.de") {
    
    body{
        background: #333 !important;
    }
    
    .wrapper .columHeader .content{
       color: #FFF;
        background: transparent;
    }
    
    .columHeader .coverimageBgColor{
       background: #35C !important;
    }
    .buttonBar.coverimageBgColor{
       background: #FFF !important;
    }
    .buttonBar button {
       color: #35C;
    }
    .buttonBar button:hover{
       color: rgba(51,85,204, 0.4);
    }
    
   
    .sidebar{
       width: 220px;
    }
   .wrapper.showLeft {
       left: 220px;
   }
   .wrapper.showLeft.hideRight .columHeader {
      box-shadow: 220px 0px 4px rgba(51, 51, 51, 0.4);
      transition: all 0.2s linear 0s;
   }
    
    .sidebar .bg{
       background: #FFF !important;
    }
    .sidebar .content{
       background: transparent !important;
    }
    .sidebar .cellContent a span {
       color: #333;
       font-weight: bold;
    }   
    .sidebar .menu b{
       color: rgba(0, 0, 0, 0.3);
    }
    .sidebar .menu button{
       color: #333;
    }
    .sidebar .menu button:hover {
       color: #FFF;
       background: #35C;
    }
    .sidebar .menu button.a::before {
       content: 'A'; /* α */
       background: #F56;
       color: #723;
       border-color: #F56;
    }
    .sidebar .menu button.b::before {
       content: 'B'; /* β */
       background: #FFEBC0;
       color: #674900;
    }
    .sidebar .menu button.a::before, .sidebar .menu button.b::before {
       border-color: transparent;
       width: auto;
       font-family: times;
       font-weight: normal;
       font-style: italic;
       font-size: 16px;
    }
    
    .sidebar .menu button.mb {
       border: 1px solid transparent;
       background: rgba(0, 0, 0, 0.1);
    }
    .sidebar .menu button.mb:hover {
       background: #35C;
    }
   
   .buttonBar button i[bubblecount]::after {
       box-shadow: 0px 4px 4px -4px rgba(51, 51, 51, 1.4);
   }
   
   
   .box.text, .box.onLine, .box.guests, .box.folder{ /* , .box.weather */
      border-color: #DFDFDF;
   }
   .box, .box{
      box-shadow: 0px 4px 8px -4px rgba(136, 136, 136, 0.4);
   }
    
   
   h3{
      color: #333;
   }
   
   hr{
      background: #D9D9D9;
   }
   
   .leaflet-container::after{
      content: '';
      position: absolute;
      top: 0;
      right: 0;
      bottom: 0;
      left: 0;
      box-shadow: inset 0 0 16px 0px rgba(0,0,0,0.1), inset 0 -4px 8px -8px rgba(0,0,0,0.1);
   }
   
   
   input, textarea, select{
      border: 1px solid rgba(0,0,0,0.1);
      box-shadow: inset 0 0 4px -2px rgba(0,0,0,0.2);
   }
   .userField input.fullWidth {
      margin: 5px -10px;
      padding-left: 36px;
      padding-right: 36px;
      box-shadow: inset 0 16px 32px -8px rgba(0,0,0,0.08),  inset 0 -16px 32px -8px rgba(0,0,0,0.08);
   }
   
   input.blue, textarea.blue, button.blue {
      border: 0 solid transparent !important;
      background: #35C;
      color: #FFF !important;
      
      box-shadow: 0px 4px 8px -4px rgba(51, 85, 204, 0.8);
   }
   input.green, textarea.green, button.green {
      border: 0 solid transparent !important;
      background: #3EC63E;
      color: #FFF !important;
      
      box-shadow: 0px 4px 8px -4px rgba(62, 198, 62, 0.8);
   }
   input.red, textarea.red, button.red {
      border: 0 solid transparent !important;
      background: #F56;
      color: #FFF !important;
      
      box-shadow: 0px 4px 8px -4px rgba(255, 85, 102, 0.8);
   }
   input.orange, textarea.orange, button.orange {
      border: 0 solid transparent !important;
      background: #FB0;
      color: #FFF !important;
      
      box-shadow: 0px 4px 8px -4px rgba(255, 187, 0, 0.8);
   }
   
   a button.blue, a button.green{
      color: #FFF !important;
   }
    
}
