css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("pivotaltracker.com") {

#layout .item { border-top:none !important; }
#layout .item .storyItem {
border:1px solid #666;
-moz-border-radius:7px;
}

#layout .item .storyPreviewHeader {
-moz-border-radius-topleft:7px;
-moz-border-radius-topright:7px;
}
#layout .item .storyDetails {
-moz-border-radius-bottomleft:7px;
-moz-border-radius-bottomright:7px;
}

.panelHeader {
color:#fff  !important;
font-size:13px !important;
font-weight:bold;
letter-spacing:1px;
text-align:center;
text-shadow:0 0 4px #000;
text-transform:uppercase;
}
.iterationHeader {
font-size:15px !important;
font-weight:bold;
text-shadow:0 0 1px #000;
}
.iterationHeader td + td + td + td {
font-size:11px;
}
.panelLink {
letter-spacing:0 !important;
text-shadow:none !important;
text-transform:none !important;
}

.storyPreviewText {
font-size:11px;
}

.storyLabels {
float:right;
font-style:normal;
}
.storyDetailsTable .storyLabels {
float:none;
}

.storyOwnerInitials {
font-weight:bold !important;
}

.storyItem {
font-size:11px;
}
.titleInputField {
font-size:12px !important;
}
.noteTableDiv {
background:#F3ECCD;
padding:0 20px 15px 15px !important;
-moz-border-radius:8px;
}
.notesTable .notesHeader { display:none; }
.notesTable .noteDate {
font-style:normal !important;
font-weight:bold;
}
.notesTextArea { margin-top:15px !important; }

}
