css:
@-moz-document domain("www.proza.ru") {
body, div, table, .toptext, .columntitle, * {
    color: #bbb;
}
body, div, table {
	background-color: #444;
    font-weight: normal;
    font-style: normal;
}

/* -Шапки, заголовки- */
h1, h2, h3, h4, .cabinethead2, .titlerec { color: #fff; }
.cabinethead1 {
	background: linear-gradient(to bottom, #333, #000);
    padding: 5px 18px;
    border-radius: 0px 14px;
    float:left;
    font-size: 20px;
    font-weight: normal;
}
.avtormenu span { 
    background-color: #fff; 
    background: linear-gradient(to bottom, #333, #000);
    padding: 2px 8px;
    border-radius: 0px 14px;
}

/* -Большинство ссылок- */
.authorlink {font-size:14px;}
a:visited, a b:visited,.rec a:visited,.notes a:visited,.anonses a:visited, .authorlink:visited {
	color: #b18e52;
    text-decoration: none;
    transition: 0.3s;
}
a, a b,.rec a,.notes a,.anonses a, .authorlink {
	color: #63b252;
    text-decoration: none;
    transition: 0.3s;
}
a:hover, a b:hover,.rec a:hover,.notes a:hover,.anonses a:hover {
	color: #fff;
    text-shadow: 0px 0px 15px #fff;
}

/* -Меню в кабинете- */
.avtormenu a { 
	color: #63b252;
    padding: 2px 8px;
    transition: 0.3s;
}
.avtormenu a:visited { color:#b18e52; }
.avtormenu a:hover { color: #fff; text-shadow: 0px 0px 15px #fff; }
.avtormenu a:visited:hover { color:#fff; text-shadow: 0px 0px 15px #fff; }

/* -Верхнее меню- */
.headlink {
	color: #f00;
    background-color: #aaa;
    padding: 20px 15px;
    margin: -9px;
    border-radius: 0px;
    text-shadow: 4px 4px 6px #666;
    transition: 0.3s;
}
.headlink:hover {
	color: #fff;
    background-color: #888;
    text-shadow: 4px 4px 6px #666;
}

/* -Таблица читателей- */
.margins table td { background-color: #333; padding:5px; }
.margins1 table:first-child td b { background-color: #222; background: linear-gradient(to top, #333, #222); border-radius: 6px 6px 0px 0px; display:block; padding:5px 10px; text-align:center;}

/* -Фотка автора, и произведений- */
#authorsphoto img, .authorsphoto img {
	border-radius: 6px;
    box-shadow: 3px 3px 6px #222;
    max-width: 800px;max-height: 400px;
}

/* -Список произведений автора- */
.poemlink {
	color: #63b252;
    padding: 2px 8px;
    transition: 0.3s;
}
.poemlink:visited { color:#b18e52; }
.poemlink:hover { color: #fff; text-shadow: 0px 0px 15px #fff; }
.poemlink:visited:hover { color:#fff; text-shadow: 0px 0px 15px #fff; }

/* -Блоки комментов- */
.rec,.notes,.anonses {
	background-color: #333;
    color: #bbb;
    border-radius: 4px 4px;
    box-shadow: 3px 3px 6px #333;
    border: 1px solid #2a2a2a;
}

/* -Допилы- */
#search {background-color: #333;}
.search {background-color: #333;}
#margins {background-color: #333;}
input,select,textarea { 
    color:#ccc; 
    background-color: #333; 
    padding:5px;
    box-shadow: 3px 3px 6px #333;
    border: 1px solid #2a2a2a;
}
option {color:#333;}
input[type=submit] {
	background-color: #222; 
    background: linear-gradient(to top, #222, #444);
    padding: 5px 20px;
    cursor:pointer;
    border-radius: 6px;
    transition: 0.3s;
}
input[type=submit]:hover {color:#fff;text-shadow: 0px 0px 15px #fff;}
.rec b { font-weight:normal; }


}
