css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("duckduckgo.com") {
  .svg .logo_homepage {
    background-image: url("https://www.google.com/images/srpr/logo11w.png") !important;
    background-size: 249px 90px !important;
  }
  #logo-wrap--home {
    display: none !important;
    background-size: 249px 90px !important;
  }
  .svg .header__logo {
    background-image: url("https://www.google.com/images/srpr/logo11w.png") !important;
    background-size: 60px 20px !important;
  }
  .header-wrap--home, .site-wrapper-border {
    display: none !important;
  }
  .header-wrap {
    border-top: 4px solid #FFFFFF !important;
  }
  .tag-home__item {
    visibility: hidden !important;
  }
  .search, search--adv, .search-wrap--home {
    padding-right: 0px !important;
    padding-left: 0px !important;
    padding: 0 0px !important;
  }
  .badge-link {
    visibility: hidden !important;
  }
  .tag-home__nav__wrap {
    visibility: hidden !important;
  }
  .svg .badge--yandex {
    background-image: url("http://www.papervisions.com/wp-content/uploads/2013/11/New-Bing-Logo-640x442.jpg") !important;
    background-size: 40px 30px !important;
  }
}
